//Callbacktohandlchangedevents
classCallBack3DSettingsChanged:publicIADLX3DSettingsChangedListener
{
public:
adlx_boolADLX_STD_CALLOn3DSettingsChanged(IADLX3DSettingsChangedEvent*p3DSettingsChangedEvent)override
{
//Get3DSettingsservice
IADLX3DSettingsServicesPtrd3dSettingSrv;
g_ADLXHelp.GetSystemServices()->Get3DSettingsServices(&d3dSettingSrv);

//GettheGPUinterface
IADLXGPUPtrgpu;
p3DSettingsChangedEvent->GetGPU(&gpu);
//RadeonSuperResolutionisaglobalfeature(theGPUinterfaceisnullptr);skipprintingitsname
if(!p3DSettingsChangedEvent->IsRadeonSuperResolutionChanged())
{
charuniqueName[128]="Unknown";
GPUUniqueName(gpu,uniqueName);
std::cout<<"GPU:"<<uniqueName<<",Getsyncevent,3Dsettingschangedeventwasfired"<<std::endl;
}
ADLX_SYNC_ORIGINorigin=p3DSettingsChangedEvent->GetOrigin();
if(origin==SYNC_ORIGIN_EXTERNAL)
{
if(p3DSettingsChangedEvent->IsAntiLagChanged())
{
//GetAntiLaginterface
IADLX3DAntiLagPtrd3dAntiLag;
d3dSettingSrv->GetAntiLag(gpu,&d3dAntiLag);
adlx_boolenabled;
d3dAntiLag->IsEnabled(&enabled);
std::cout<<"\tAntiLagchanged\n\tIsEnabled:"<<enabled<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsChillChanged())
{
//GetChillinterface
IADLX3DChillPtrd3dChill;
d3dSettingSrv->GetChill(gpu,&d3dChill);
adlx_boolenabled;
d3dChill->IsEnabled(&enabled);
adlx_intminFPS,maxFPS;
d3dChill->GetMinFPS(&minFPS);
d3dChill->GetMaxFPS(&maxFPS);
std::cout<<"\tChillchanged\n\tIsEnabled:"<<enabled<<",FPS:["<<minFPS<<","<<maxFPS<<"]"<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsBoostChanged())
{
//GetBoostinterface
IADLX3DBoostPtrd3dBoost;
d3dSettingSrv->GetBoost(gpu,&d3dBoost);
adlx_boolenabled;
d3dBoost->IsEnabled(&enabled);
adlx_intminRes;
d3dBoost->GetResolution(&minRes);
std::cout<<"\tBoostchanged\n\tIsEnabled:"<<enabled<<",minResolution:"<<minRes<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsImageSharpeningChanged())
{
//GetImageSharpeninterface
IADLX3DImageSharpeningPtrd3dImageSharpen;
d3dSettingSrv->GetImageSharpening(gpu,&d3dImageSharpen);
adlx_boolenabled;
d3dImageSharpen->IsEnabled(&enabled);
adlx_intmin;
d3dImageSharpen->GetSharpness(&min);
std::cout<<"\tImageSharpeningchanged\n\tIsEnabled:"<<enabled<<",Sharpness:"<<min<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsEnhancedSyncChanged())
{
//GetEnhancedSyncinterface
IADLX3DEnhancedSyncPtrd3dEnhancedSync;
d3dSettingSrv->GetEnhancedSync(gpu,&d3dEnhancedSync);
adlx_boolenabled;
d3dEnhancedSync->IsEnabled(&enabled);
std::cout<<"\tEnhancedSyncchanged\n\tIsEnabled:"<<enabled<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsWaitForVerticalRefreshChanged())
{
//GetVsyncinterface
IADLX3DWaitForVerticalRefreshPtrvsync;
d3dSettingSrv->GetWaitForVerticalRefresh(gpu,&vsync);
adlx_boolenabled;
vsync->IsEnabled(&enabled);
ADLX_WAIT_FOR_VERTICAL_REFRESH_MODEmode=WFVR_ALWAYS_OFF;
vsync->GetMode(&mode);
std::cout<<"\tWaitForVerticalRefreshchanged\n\tIsEnabled:"<<enabled<<",Mode:"<<vsyncMode.find(mode)->second<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsFrameRateTargetControlChanged())
{
//GetFRTCinterface
IADLX3DFrameRateTargetControlPtrfrtc;
d3dSettingSrv->GetFrameRateTargetControl(gpu,&frtc);
adlx_boolenabled;
frtc->IsEnabled(&enabled);
adlx_intfps;
frtc->GetFPS(&fps);
std::cout<<"\tFRTCchanged\n\tIsEnabled:"<<enabled<<",fps:"<<fps<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsAntiAliasingChanged())
{
//GetAntiAliasinginterface
IADLX3DAntiAliasingPtrantiAliasing;
d3dSettingSrv->GetAntiAliasing(gpu,&antiAliasing);
ADLX_ANTI_ALIASING_MODEmode;
antiAliasing->GetMode(&mode);
ADLX_ANTI_ALIASING_METHODmethod;
antiAliasing->GetMethod(&method);
ADLX_ANTI_ALIASING_LEVELlevel;
antiAliasing->GetLevel(&level);
std::cout<<"\tAntiAliasingchanged\n"<<std::endl
<<"\tMode:"<<antiAliasingMode.find(mode)->second<<std::endl
<<"\tMethod:"<<antiAliasingMethod.find(method)->second<<std::endl
<<"\tLevel:"<<antiAliasingLevel.find(level)->second<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsMorphologicalAntiAliasingChanged())
{
//GetMorphologicAntiAliasinginterface
IADLX3DMorphologicalAntiAliasingPtrmAntiAliasing;
d3dSettingSrv->GetMorphologicalAntiAliasing(gpu,&mAntiAliasing);
adlx_boolenabled;
mAntiAliasing->IsEnabled(&enabled);
std::cout<<"\tMorphologicalAntiAliasingchanged\n\tIsEnabled:"<<enabled<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsAnisotropicFilteringChanged())
{
//GetAnisotropicFilteringPtrinterface
IADLX3DAnisotropicFilteringPtranisotropicFiltering;
d3dSettingSrv->GetAnisotropicFiltering(gpu,&anisotropicFiltering);
adlx_boolenabled;
anisotropicFiltering->IsEnabled(&enabled);
ADLX_ANISOTROPIC_FILTERING_LEVELlevel;
anisotropicFiltering->GetLevel(&level);
std::cout<<"\tAnisotropicFilteringchanged\n\tIsEnabled:"<<enabled<<std::endl
<<"\tLevel:"<<levelMap.find(level)->second<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsTessellationModeChanged())
{
//GetIADLX3DTessellationinterface
IADLX3DTessellationPtrtessellation;
d3dSettingSrv->GetTessellation(gpu,&tessellation);
ADLX_TESSELLATION_MODEmode;
tessellation->GetMode(&mode);
ADLX_TESSELLATION_LEVELlevel;
tessellation->GetLevel(&level);
std::cout<<"\tTessellationModechanged\n"<<std::endl
<<"\tMode:"<<Modes.find(mode)->second<<std::endl
<<"\tLevel:"<<Levels.find(level)->second<<std::endl;
}
elseif(p3DSettingsChangedEvent->IsResetShaderCache())
{
std::cout<<"\tResetShaderCache"<<std::endl;
}
}

if(origin==SYNC_ORIGIN_UNKNOWN)
{
if(p3DSettingsChangedEvent->IsRadeonSuperResolutionChanged())
{
//GetIADLX3DRadeonSuperResolutioninterface
IADLX3DRadeonSuperResolutionPtrrsr;
d3dSettingSrv->GetRadeonSuperResolution(&rsr);
adlx_boolenabled=false;
rsr->IsEnabled(&enabled);
adlx_intsharpness;
rsr->GetSharpness(&sharpness);
std::cout<<"\tRSRchanged\n\tIsEnabled:"<<enabled<<",Sharpness:"<<sharpness<<std::endl;
}
}

SetEvent(blockEvent);

//ReturntrueforADLXtocontinuenotifyingthenextlistener,orfalsetostopnotification
returntrue;
}
};

//Waitforquitsignal
voidQuitWait(adlx_bool*loopFlag)
{
while(true)
{
//Non-blockingcheckingiftheI/Ocachehascharacters
if(_kbhit())
{
charc;
if((c=getchar())=='q'||c=='Q')
{
*loopFlag=false;
SetEvent(quitEvent);
break;
}
}
Sleep(100);
}
}

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Createblockevent
blockEvent=CreateEvent(NULL,TRUE,FALSE,NULL);
//Createquitevent
quitEvent=CreateEvent(NULL,TRUE,FALSE,NULL);
//Createeventarray
HANDLEeventArray[]={blockEvent,quitEvent};

//Get3DSettingsservice
IADLX3DSettingsServicesPtrd3dSettingSrv;
res=g_ADLXHelp.GetSystemServices()->Get3DSettingsServices(&d3dSettingSrv);

if(ADLX_SUCCEEDED(res))
{
//GetChangehandle
IADLX3DSettingsChangedHandlingPtrchangeHandle;
res=d3dSettingSrv->Get3DSettingsChangedHandling(&changeHandle);
if(ADLX_SUCCEEDED(res))
{
//Createcallback
IADLX3DSettingsChangedListener*call=newCallBack3DSettingsChanged;

//Addcallback
changeHandle->Add3DSettingsEventListener(call);

//Createathreadtocheckquitevent
boolloopFlag=true;
std::threadt{QuitWait,&loopFlag};

std::cout<<"\nWaitingfor3DSettingchangeevent...PressQ/qtoquit"<<std::endl;
while(true)
{
//Waitforchangeeventorrequesttoquit
DWORDwaitRet=WaitForMultipleObjects(2,eventArray,FALSE,INFINITE);
if(!loopFlag)
{
if(t.joinable())
{
t.join();
}
break;
}
ResetEvent(blockEvent);
}

//Removecallback
changeHandle->Remove3DSettingsEventListener(call);

//Deletecallback
deletecall;
call=nullptr;
}
}
else
{
std::cout<<"Failedtogetthe3DSettingsServices"<<std::endl;
}
}
else
{
std::cout<<"ADLXinitializationfailed"<<std::endl;
return0;
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Closeevent
if(blockEvent)
CloseHandle(blockEvent);
if(quitEvent)
CloseHandle(quitEvent);

//Pausetoseetheprintout
system("pause");

return0;
}

voidGPUUniqueName(IADLXGPUPtrgpu,char*uniqueName)
{
if(nullptr!=gpu&&nullptr!=uniqueName)
{
constchar*gpuName=nullptr;
gpu->Name(&gpuName);
adlx_intid;
gpu->UniqueId(&id);
sprintf_s(uniqueName,128,"name:%s,id:%d",gpuName,id);
}
}
