//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance.
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Blockeventtoverifycallback
HANDLEdisplaySettingsEvent=nullptr;
HANDLEdisplay3DLUTEvent=nullptr;
//Quiteventtoexit
HANDLEquitEvent=nullptr;

//Getthedisplayuniquename
voidDisplayUniqueName(IADLXDisplayPtrdisplay,char*uniqueName);

//Callbacktohandlechangedevents
classCallBackDisplaySettingsChanged:publicIADLXDisplaySettingsChangedListener
{
public:
adlx_boolADLX_STD_CALLOnDisplaySettingsChanged(IADLXDisplaySettingsChangedEvent*pDisplaySettingsChangedEvent)override
{
ADLX_SYNC_ORIGINorigin=pDisplaySettingsChangedEvent->GetOrigin();
IADLXDisplaySettingsChangedEvent1PtrpDisplaySettingChangedEvent1(pDisplaySettingsChangedEvent);
if(nullptr==pDisplaySettingChangedEvent1)
{
std::cout<<"IADLXDisplaySettingsChangedEvent1notsupported"<<std::endl;
}

if(origin==SYNC_ORIGIN_EXTERNAL)
{
IADLXDisplayServicesPtrdisplayServices;
g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayServices);
IADLXDisplayPtrdisplay;
pDisplaySettingsChangedEvent->GetDisplay(&display);
chardisplayName[128]="Unknown";
DisplayUniqueName(display,displayName);
if(pDisplaySettingsChangedEvent->IsColorDepthChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,colordepthischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsCustomColorBrightnessChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,customcolorbrightnessischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsCustomColorContrastChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,customcolorcontrastischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsCustomColorHueChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,customcolorhueischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsCustomColorSaturationChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,customcolorsaturationischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsCustomColorTemperatureChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,customcolortemperatureischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsCustomResolutionChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,customresolutionischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsFreeSyncChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,freesyncischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsGPUScalingChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,GPUscalingischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsHDCPChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,HDCPischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsIntegerScalingChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,integerscalingischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsPixelFormatChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,pixelischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsScalingModeChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,scalingmodeischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsVariBrightChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,varibrightischanged"<<std::endl;
}
elseif(pDisplaySettingsChangedEvent->IsVSRChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,VSRischanged"<<std::endl;
}

if(pDisplaySettingChangedEvent1)
{
if(pDisplaySettingChangedEvent1->IsDisplayBlankingChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,displayblankingischanged"<<std::endl;
}
}

}
SetEvent(displaySettingsEvent);

//Returntruefor ADLXto continue notifyingthenextlistener,or falsetostop notification.
returntrue;
}
};

classCallBackDisplay3DLUTChanged:publicIADLXDisplay3DLUTChangedListener
{
public:
adlx_boolADLX_STD_CALLOnDisplay3DLUTChanged(IADLXDisplay3DLUTChangedEvent*pDisplay3DLUTChangedEvent)override
{
ADLX_SYNC_ORIGINorigin=pDisplay3DLUTChangedEvent->GetOrigin();
if(origin==SYNC_ORIGIN_EXTERNAL)
{
IADLXDisplayPtrdisplay;
pDisplay3DLUTChangedEvent->GetDisplay(&display);
chardisplayName[128]="Unknown";
DisplayUniqueName(display,displayName);
if(pDisplay3DLUTChangedEvent->IsSCEChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,SCEischanged"<<std::endl;
}
elseif(pDisplay3DLUTChangedEvent->IsCustom3DLUTChanged())
{
std::cout<<"Display"<<displayName<<"Getsyncevent,custom3DLUTischanged"<<std::endl;
}
}
SetEvent(display3DLUTEvent);

//Returntruefor ADLXto continue notifyingthenextlistener,or falsetostop notification
returntrue;
}
};

//Waitforexitwitherrormessage
voidQuitWait(adlx_bool*loopFlag)
{
while(true)
{
//Non-blockingcheckingiftheI/Ocachehascharacters
if(_kbhit())
{
charc;
if((c=getchar())=='q'||c=='Q')
{
*loopFlag=false;
SetEvent(quitEvent);
break;
}
}
Sleep(100);
}
}

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Createblockevent
displaySettingsEvent=CreateEvent(NULL,FALSE,FALSE,NULL);
display3DLUTEvent=CreateEvent(NULL,FALSE,FALSE,NULL);
//Createquitevent
quitEvent=CreateEvent(NULL,TRUE,FALSE,NULL);
//Createeventarray
HANDLEeventArray[]={displaySettingsEvent,display3DLUTEvent,quitEvent};

//Getdisplayservice
IADLXDisplayServicesPtrdisplayServices;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayServices);

if(ADLX_SUCCEEDED(res))
{
//GetChangehandle
IADLXDisplayChangedHandlingPtrchangeHandle;
res=displayServices->GetDisplayChangedHandling(&changeHandle);
if(ADLX_SUCCEEDED(res))
{
//Createcallback
IADLXDisplaySettingsChangedListener*displaySettingsChangedCallBack=newCallBackDisplaySettingsChanged;
IADLXDisplay3DLUTChangedListener*display3DLUTChangedCallBack=newCallBackDisplay3DLUTChanged;

//Addcallback
changeHandle->AddDisplaySettingsEventListener(displaySettingsChangedCallBack);
changeHandle->AddDisplay3DLUTEventListener(display3DLUTChangedCallBack);

//Createathreadtocheckquitevent
boolloopFlag=true;
std::threadt{QuitWait,&loopFlag};

std::cout<<"\nWaitingfordisplaychangeevent...PressQ/qtoquit\n"<<std::endl;
while(true)
{
//Waitforchangeeventorrequesttoquit
DWORDwaitRet=WaitForMultipleObjects(3,eventArray,FALSE,INFINITE);
if(!loopFlag)
{
if(t.joinable())
{
t.join();
}
break;
}
}

//Removecallback
changeHandle->RemoveDisplaySettingsEventListener(displaySettingsChangedCallBack);
changeHandle->RemoveDisplay3DLUTEventListener(display3DLUTChangedCallBack);

//Deletecallback
deletedisplaySettingsChangedCallBack;
deletedisplay3DLUTChangedCallBack;
displaySettingsChangedCallBack=nullptr;
display3DLUTChangedCallBack=nullptr;
}
}
else
{
std::cout<<"FailedtogettheDisplayServices"<<std::endl;
}
}
else
{
std::cout<<"ADLXinitializationfailed"<<std::endl;
return0;
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Closeevent
if(displaySettingsEvent)
CloseHandle(displaySettingsEvent);
if(display3DLUTEvent)
CloseHandle(display3DLUTEvent);
if(quitEvent)
CloseHandle(quitEvent);

//Pausetoseetheprintout
system("pause");

return0;
}

voidDisplayUniqueName(IADLXDisplayPtrdisplay,char*uniqueName)
{
if(nullptr!=display&&nullptr!=uniqueName)
{
constchar*gpuName=nullptr;
display->Name(&gpuName);
adlx_sizeid;
display->UniqueId(&id);
sprintf_s(uniqueName,128,"name:%s,id:%zu",gpuName,id);
}
}
