#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"
#include<iostream>
#include<string>
#include<unordered_map>
#include<algorithm>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface. 
staticADLXHelperg_ADLXHelp;

//DisplayDisplayColorDepthSupport
voidShowDisplayColorDepthSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Displaycurrentdisplaycolordepthstate
voidGetColorDepthState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Setdisplaycolordepth
voidSetColorDepthState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService,display);
}
}
else
{
std::cout<<"Failedtogetthedisplaylist"<<std::endl;
}
}
else
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;
returnWaitAndExit("Failedtogetdisplayservices",0);
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pauseto seetheprintout
system("Pause");

return0;
}

voidShowDisplayColorDepthSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
IADLXDisplayColorDepthPtrdisplayColorDepth;
ADLX_RESULTres=displayService->GetColorDepth(display,&displayColorDepth);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Getcolordepthsupportedstatus==="<<std::endl;
adlx_boolsupported=false;
res=displayColorDepth->IsSupported(&supported);
std::cout<<"\tIsSupported,res"<<res<<",supported:"<<supported<<std::endl;
}
}
voidGetSupportStateForEachColorDepth(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
IADLXDisplayColorDepthPtrdisplayColorDepth;
ADLX_RESULTres=displayService->GetColorDepth(display,&displayColorDepth);
if(ADLX_SUCCEEDED(res)&&displayColorDepth)
{
std::cout<<"===Getcolordepthsupportlist==="<<std::endl;
adlx_boolisSupported=false;
res=displayColorDepth->IsSupportedBPC_6(&isSupported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedBPC_6,result"<<res<<"BPC_6support:"<<isSupported<<std::endl;
res=displayColorDepth->IsSupportedBPC_8(&isSupported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedBPC_6,result"<<res<<"BPC_8support:"<<isSupported<<std::endl;
res=displayColorDepth->IsSupportedBPC_10(&isSupported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedBPC_10,result"<<res<<"BPC_10support:"<<isSupported<<std::endl;
res=displayColorDepth->IsSupportedBPC_12(&isSupported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedBPC_12,result"<<res<<"BPC_12support:"<<isSupported<<std::endl;
res=displayColorDepth->IsSupportedBPC_14(&isSupported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedBPC_14,result"<<res<<"BPC_14support:"<<isSupported<<std::endl;
res=displayColorDepth->IsSupportedBPC_16(&isSupported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedBPC_16,result"<<res<<"BPC_16support:"<<isSupported<<std::endl;

conststd::unordered_map<ADLX_COLOR_DEPTH,std::string>colorDepthMap{
{BPC_6,"BPC_6"},
{BPC_8,"BPC_8"},
{BPC_10,"BPC_10"},
{BPC_12,"BPC_12"},
{BPC_14,"BPC_14"},
{BPC_16,"BPC_16"}
};
for_each(colorDepthMap.begin(),colorDepthMap.end(),[&](autocolorDepth){
res=displayColorDepth->IsSupportedColorDepth(colorDepth.first,&isSupported);
std::cout<<"\tIsSupported"<<colorDepth.second<<":"<<isSupported<<std::endl;
});
}
}

voidGetColorDepthState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
conststd::unordered_map<int,std::string>colorDepthMap={{0,"BPC_UNKNOWN"},{1,"BPC_6"},{2,"BPC_8"},{3,"BPC_10"}};
IADLXDisplayColorDepthPtrdisplayColorDepth;
ADLX_RESULTres=displayService->GetColorDepth(display,&displayColorDepth);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Getcolordepthenabledstatus==="<<std::endl;
ADLX_COLOR_DEPTHcd=BPC_UNKNOWN;
res=displayColorDepth->GetValue(&cd);
std::stringcdStr="BPC_Unknown";
autosearchIter=colorDepthMap.find(cd);
if(searchIter!=colorDepthMap.end())
{
cdStr=searchIter->second;
}
std::cout<<"\tGetValue,result"<<res<<"colordepth:"<<cdStr.c_str()<<std::endl;
}
}

voidSetColorDepthState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey)
{
IADLXDisplayColorDepthPtrdisplayColorDepth;
ADLX_RESULTres=displayService->GetColorDepth(display,&displayColorDepth);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Setcolordepth==="<<std::endl;
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//setColorDepthBPC_6
case0:
res=displayColorDepth->SetValue(BPC_6);
break;
//setColorDepthBPC_8
case1:
res=displayColorDepth->SetValue(BPC_8);
break;
//setColorDepthBPC_10
case2:
res=displayColorDepth->SetValue(BPC_10);
break;
default:
break;
}
std::cout<<"\tReturncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}
}

voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1todisplaycolordepthsupport"<<std::endl;

std::cout<<"\t->Press2togetcolordepthstate"<<std::endl;

std::cout<<"\t->Press3tosetcurrentcolordepthtoBPC_6"<<std::endl;
std::cout<<"\t->Press4tosetcurrentcolordepthtoBPC_8"<<std::endl;
std::cout<<"\t->Press5tosetcurrentcolordepthtoBPC_10"<<std::endl;

std::cout<<"\t->Press6togetsupportstateforeachcolordepth"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayDisplayColorDepthSupport
case'1':
ShowDisplayColorDepthSupport(displayService,display);
break;

//Displaycurrentdisplaycolordepthstate
case'2':
GetColorDepthState(displayService,display);
break;

//Setdisplaycolordepth
case'3':
case'4':
case'5':
SetColorDepthState(displayService,display,num-'3');
break;

case'6':
GetSupportStateForEachColorDepth(displayService,display);
break;
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbefore returningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("Pause");
returnretCode;
}
