//UseADLXnamespace
usingnamespaceadlx;

//TypesofCustomColor
constexprautoCUSTOM_COLOR_HUE=1;
constexprautoCUSTOM_COLOR_SATURATION=2;
constexprautoCUSTOM_COLOR_BRIGHTNESS=3;
constexprautoCUSTOM_COLOR_CONTRAST=4;
constexprautoCUSTOM_COLOR_TEMPERATURE=5;

//ADLXHelperinstance.
//NooutstandinginterfacesfromADLXmustexistwhenADLXdestory.
//soweuseglobalvariablestoensurethevalidoftheinterface.
staticADLXHelperg_ADLXHelp;

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//ExecutorclassforgettingandsettingCustomColorproperties
classCustomColorExecutor;

//Handlerforuserinput
voidExecuteCommand(CustomColorExecutor&executor,conststd::string&colorTypeString);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService,display);
}
}
else
{
std::cout<<"Failedtogetthedisplaylist"<<std::endl;
}
}
else
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;
returnWaitAndExit("Failedtogetdisplayservices",0);
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pauseto seetheprintout
system("Pause");

return0;
}

classCustomColorExecutor
{
public:
CustomColorExecutor()=default;
CustomColorExecutor(IADLXDisplayCustomColorPtrpInst,intct)
:pDisplayColor_(pInst),colorType_(ct)
{
if(pInst==nullptr)
{
std::cout<<"customcolorexecutor:customcolorinstanceisnull!"<<std::endl;
std::exit(0);
}
}
ADLX_RESULTGetSupport(adlx_bool*supported)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->IsHueSupported(supported);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->IsSaturationSupported(supported);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->IsBrightnessSupported(supported);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->IsContrastSupported(supported);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->IsTemperatureSupported(supported);
}
returnADLX_FAIL;
}
ADLX_RESULTGetRange(ADLX_IntRange*range)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->GetHueRange(range);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->GetSaturationRange(range);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->GetBrightnessRange(range);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->GetContrastRange(range);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->GetTemperatureRange(range);
}
returnADLX_FAIL;
}
ADLX_RESULTGetValue(adlx_int*value)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->GetHue(value);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->GetSaturation(value);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->GetBrightness(value);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->GetContrast(value);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->GetTemperature(value);
}
returnADLX_FAIL;
}
ADLX_RESULTSetValue(adlx_intvalue)
{
switch(colorType_)
{
caseCUSTOM_COLOR_HUE:
returnpDisplayColor_->SetHue(value);
caseCUSTOM_COLOR_SATURATION:
returnpDisplayColor_->SetSaturation(value);
caseCUSTOM_COLOR_BRIGHTNESS:
returnpDisplayColor_->SetBrightness(value);
caseCUSTOM_COLOR_CONTRAST:
returnpDisplayColor_->SetContrast(value);
caseCUSTOM_COLOR_TEMPERATURE:
returnpDisplayColor_->SetTemperature(value);
}
returnADLX_FAIL;
}

private:
intcolorType_;
IADLXDisplayCustomColorPtrpDisplayColor_;
};

voidExecuteCommand(CustomColorExecutor&executor,conststd::string&colorTypeString)
{
std::cout<<"\tChoosefromthefollowingoptions:"<<colorTypeString.c_str()<<"settings"<<std::endl;
std::cout<<"\t->Press1todisplaysupport"<<std::endl;
std::cout<<"\t->Press2togetrangedata"<<std::endl;
std::cout<<"\t->Press3todisplaycurrentvalue"<<std::endl;
std::cout<<"\t->Press4tosetcurrentvalue"<<std::endl;
std::cout<<"\t->PressX/xtogetbacktomainmenu"<<std::endl;

intnum=0;
while((num=getchar())!='x'&&num!='X')
{
switch(num)
{
case'1':
{
std::stringstr1="===Get"+colorTypeString+"Supported===";
std::stringstr2="\tIs"+colorTypeString+"Supported:";
adlx_boolsupported=false;
std::cout<<str1.c_str()<<std::endl;
executor.GetSupport(&supported);
std::cout<<str2.c_str()<<supported<<std::endl;
break;
}
case'2':
{
std::stringstr1="===Get"+colorTypeString+"Rangedata===";
std::stringstr2="\tGet"+colorTypeString+"Range:";
ADLX_IntRangerange={0};
std::cout<<"===GetHueRangedata==="<<std::endl;
executor.GetRange(&range);
std::cout<<str2.c_str()<<"min:"<<range.minValue<<"max:"<<range.maxValue<<"step:"<<range.step<<std::endl;
break;
}
case'3':
{
std::stringstr1="===Getcurrent"+colorTypeString+"value===";
std::stringstr2="\tGet"+colorTypeString+":";
adlx_intvalue=0;
std::cout<<str1.c_str()<<std::endl;
executor.GetValue(&value);
std::cout<<str2.c_str()<<"value:"<<value<<std::endl;
break;
}
case'4':
{
ADLX_IntRangerange={0};
executor.GetRange(&range);
adlx_intcurrentValue=0;
executor.GetValue(&currentValue);
adlx_intbaseValue=(range.minValue+range.maxValue)/2;
adlx_intvalue=(currentValue==baseValue)?baseValue+range.step:baseValue;
std::stringstr1="\tSet"+colorTypeString+":";
std::cout<<str1.c_str()<<"value:"<<value<<std::endl;
autores=executor.SetValue(value);
std::cout<<"\tReturncode(0isSuccess)is:"<<res<<"(0meanssuccess)"<<std::endl;
break;
}
default:
break;
}
}
MainMenu();
}

voidShowCustomColorSettings(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintcolorType)
{
staticconststd::unordered_map<int,std::string>colorTypeStringMap=
{
{CUSTOM_COLOR_HUE,"Hue"},
{CUSTOM_COLOR_SATURATION,"Saturation"},
{CUSTOM_COLOR_BRIGHTNESS,"Brightness"},
{CUSTOM_COLOR_CONTRAST,"Contrast"},
{CUSTOM_COLOR_TEMPERATURE,"Temperature"}};

IADLXDisplayCustomColorPtrdisplayCustomColor;
ADLX_RESULTres=displayService->GetCustomColor(display,&displayCustomColor);
if(ADLX_SUCCEEDED(res))
{
autoexecutor=CustomColorExecutor(displayCustomColor,colorType);
try
{
ExecuteCommand(executor,colorTypeStringMap.at(colorType));
}catch(...)
{
std::cout<<"Showcustomcolorsettings:colortypeisinvalid!"<<std::endl;
}
}
else
{
std::cout<<"GetCustomColor:failed."<<std::endl;
}
}

voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1toenterHuesettings"<<std::endl;
std::cout<<"\t->Press2toenterSaturationsettings"<<std::endl;
std::cout<<"\t->Press3toenterBrightnesssettings"<<std::endl;
std::cout<<"\t->Press4toenterContrastsettings"<<std::endl;
std::cout<<"\t->Press5toenterTemperaturesettings"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaythecommandpromptmenu"<<std::endl;
}

voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
case'1':
ShowCustomColorSettings(displayService,display,CUSTOM_COLOR_HUE);
break;
//DisplaySaturationsettings
case'2':
ShowCustomColorSettings(displayService,display,CUSTOM_COLOR_SATURATION);
break;
//DisplayBrightnesssettings
case'3':
ShowCustomColorSettings(displayService,display,CUSTOM_COLOR_BRIGHTNESS);
break;
//DisplayContrastsettings
case'4':
ShowCustomColorSettings(displayService,display,CUSTOM_COLOR_CONTRAST);
break;
//DisplayTemperaturesettings
case'5':
ShowCustomColorSettings(displayService,display,CUSTOM_COLOR_TEMPERATURE);
break;
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("Pause");
returnretCode;
}
