#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"
#include<iostream>
#include<string>
#include<unordered_map>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//Displaycustomresolutionsupport
voidShowCustomResolutionSupport(IADLXDisplayCustomResolutionPtrpCustomResolution);

//Displayavailabledisplayresolutions
voidGetResolutionList(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolutionList**ppResolutionList);

//Displaycurrentdisplayresolution
voidGetCurrentResolution(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolution**ppResolution);

//Createnewcustomizeddisplayresolution
voidCreateNewResolution(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolutionPtrpResolution);

//Deletedisplayresolution
voidDeleteResolution(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolutionPtrpResolution);

//Printoutspecifiedresolution
voidPrintResolution(IADLXDisplayResolutionPtrpResolution);

//Printoutspecifiedresolutionlist
voidPrintResolutions(IADLXDisplayResolutionListPtrpResolutionList);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService,display);
}
}
else
{
std::cout<<"Failedtogetthedisplaylist"<<std::endl;
}
}
else
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("FailedtogettheDisplayServices",0);
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pausetoseetheprintout
system("Pause");

return0;
}

voidShowCustomResolutionSupport(IADLXDisplayCustomResolutionPtrpCustomResolution)
{
std::cout<<"===customresolutionsupported==="<<std::endl;
adlx_boolsupported=false;
ADLX_RESULTres=pCustomResolution->IsSupported(&supported);
std::cout<<"\tIsSupportedresult:"<<res<<",supported:"<<supported<<std::endl;
}

voidGetResolutionList(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolutionList**ppResolutionList)
{
ADLX_RESULTres=pCustomResolution->GetResolutionList(ppResolutionList);
if(ADLX_FAILED(res))
std::cout<<"GetResolutionList:failed"<<std::endl;

}

voidGetCurrentResolution(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolution**ppResolution)
{
ADLX_RESULTres=pCustomResolution->GetCurrentAppliedResolution(ppResolution);
if(ADLX_FAILED(res))
std::cout<<"GetCurrentAppliedResolution:failed"<<std::endl;
}

voidCreateNewResolution(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolutionPtrpResolution)
{
ADLX_RESULTres=pCustomResolution->CreateNewResolution(pResolution);
if(ADLX_FAILED(res))
std::cout<<"CreateNewResolution:failed."<<std::endl;
}

voidDeleteResolution(IADLXDisplayCustomResolutionPtrpCustomResolution,IADLXDisplayResolutionPtrpResolution)
{
PrintResolution(pResolution);
ADLX_RESULTres=pCustomResolution->DeleteResolution(pResolution);
if(ADLX_FAILED(res))
std::cout<<"DeleteResolution:failed."<<std::endl;
}

voidPrintResolution(IADLXDisplayResolutionPtrpResolution)
{
ADLX_CustomResolutioncr;
pResolution->GetValue(&cr);

std::stringsPresention=(cr.presentation==ADLX_DISPLAY_SCAN_TYPE::INTERLACED)?"Interlaced":"Progressive";
std::stringsTimingStandard="Manual";
std::stringsHPolarity=(cr.detailedTiming.hPolarity==ADLX_DISPLAY_TIMING_POLARITY::POSITIVE)?"Positive":"Negative";
std::stringsVPolarity=(cr.detailedTiming.vPolarity==ADLX_DISPLAY_TIMING_POLARITY::POSITIVE)?"Positive":"Negative";
try
{
conststd::unordered_map<ADLX_TIMING_STANDARD,std::string>timingStandardMap=
{
{MANUAL,"Manual"},{DMT,"DMT"},{GTF,"GTF"},{CVT_RB,"CVT-ReducedBlanking"},{CVT,"CVT"}};

sTimingStandard=timingStandardMap.at(cr.timingStandard);
}catch(...)
{
std::cout<<"Timingstandardoutofrange!"<<std::endl;
return;
}

charbuff[1024]={0};
intoffset=std::snprintf(buff,sizeof(buff),"\nresolution(Px)\t\t%dx%d\n",cr.resWidth,cr.resHeight);
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"RefreshRate(Hz)\t\t%d\n",cr.refreshRate);
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"Presentation\t\t\t%s\n",sPresention.c_str());
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"TimingStandard\t\t%s\n",sTimingStandard.c_str());
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"G.PixelClock(kHz)\t\t%d\n",cr.GPixelClock);
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"TimingInfo\t\t\tHorizontal\tVertical\n");
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"TimingTotal\t\t\t%d\t%d\n",cr.detailedTiming.hTotal,cr.detailedTiming.vTotal);
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"TimingDisplay\t\t%d\t%d\n",cr.detailedTiming.hDisplay,cr.detailedTiming.vDisplay);
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"TimingFrontPorch\t\t%d\t%d\n",cr.detailedTiming.hFrontPorch,cr.detailedTiming.vFrontPorch);
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"TimingSyncWidth\t\t%d\t%d\n",cr.detailedTiming.hSyncWidth,cr.detailedTiming.vSyncWidth);
offset+=std::snprintf(buff+offset,sizeof(buff)-offset,"TimingPolarity\t\t%s%s\n",sHPolarity.c_str(),sVPolarity.c_str());

std::cout<<buff<<std::endl;
}

voidPrintResolutions(IADLXDisplayResolutionListPtrpResolutionList)
{
std::cout<<"Printoutresolutionlist=="<<std::endl;
if(pResolutionList==nullptr)
{
std::cout<<"resolutionlistisnull"<<std::endl;
return;
}

if(pResolutionList->Size()==0)
{
std::cout<<"resolutionlistisempty!"<<std::endl;
return;
}

ADLX_RESULTres=ADLX_FAIL;
for(adlx_uintcrt=0;crt<pResolutionList->Size();crt++)
{
IADLXDisplayResolutionPtrpDispResolution=nullptr;
res=pResolutionList->At(crt,&pDispResolution);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"resolutionindex:"<<crt<<std::endl;
PrintResolution(pDispResolution);
}
}
}

voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions"<<std::endl;

std::cout<<"\t->Press1todisplaycustomResolutionsupport"<<std::endl;

std::cout<<"\t->Press2togetcustomResolutionlist"<<std::endl;

std::cout<<"\t->Press3togetcurrentappliedResolution"<<std::endl;
std::cout<<"\t->Press4tocreatenewcustomizedResolution"<<std::endl;
std::cout<<"\t->Press5todeleteavaibleResolution"<<std::endl;

std::cout<<"\t->PressQ/qterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaythecommandpromptmenu"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
IADLXDisplayCustomResolutionPtrpCustomResolution;
displayService->GetCustomResolution(display,&pCustomResolution);

if(!pCustomResolution)
{
std::cout<<"Menucontrol:cannotretrivecustomresolutioninstance!"<<std::endl;
return;
}

intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaycustomresolutionsupport
case'1':
ShowCustomResolutionSupport(pCustomResolution);
break;

//Getcurrentresolution
case'2':
{
std::cout<<"===Getresolutionlist:==="<<std::endl;
IADLXDisplayResolutionListPtrpResolutionList;
GetResolutionList(pCustomResolution,&pResolutionList);
PrintResolutions(pResolutionList);
break;
}

//Getcurrentappliedresolution
case'3':
{
std::cout<<"===Getcurrentresolution:==="<<std::endl;
IADLXDisplayResolutionPtrpDisplayResolution;
GetCurrentResolution(pCustomResolution,&pDisplayResolution);
PrintResolution(pDisplayResolution);
break;
}
//Createnewcustomizedresolution
case'4':
{
std::cout<<"===Createnewresolution:==="<<std::endl;
IADLXDisplayResolutionPtrpDisplayResolution;
GetCurrentResolution(pCustomResolution,&pDisplayResolution);

if(pDisplayResolution)
{
ADLX_CustomResolutioncustomRes={};
pDisplayResolution->GetValue(&customRes);

customRes.resWidth=1920;
customRes.resHeight=1080;
customRes.detailedTiming.hDisplay=1920;
customRes.detailedTiming.vDisplay=1080;
pDisplayResolution->SetValue(customRes);

PrintResolution(pDisplayResolution);
CreateNewResolution(pCustomResolution,pDisplayResolution);
}
break;
}
//Deleteavailableresolution
case'5':
{
std::cout<<"===Deleteresolution:==="<<std::endl;
IADLXDisplayResolutionListPtrpResolutionList;
GetResolutionList(pCustomResolution,&pResolutionList);
if(pResolutionList->Size()>0)
{
IADLXDisplayResolutionPtrpDisplayResolution;
ADLX_RESULTres=pResolutionList->At(0,&pDisplayResolution);
if(ADLX_SUCCEEDED(res))
DeleteResolution(pCustomResolution,pDisplayResolution);
}
break;
}
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("Pause");
returnretCode;
}
