usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestoryed.
//soweuseglobalvariablestoensurethevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//DisplaysupportedHDMIqualitydetection
voidShowSupportedHDMIQualityDetection(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//DisplaysupportedDPlink
voidShowSupportedDPLink(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//DisplaycurrentHDMIqualitydetectionstate
voidGetEnabledHDMIQualityDetection(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//SetdisplayVariBrightstatustoenable/disable
voidSetEnabledHDMIQualityDetection(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display,adlx_boolenableDisable);

//DisplayDPlinkratevalue
voidGetDPLinkRate(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Displaynumberofactivelanes
voidGetNumberOfActiveLanes(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Displaynumberoftotallanes
voidGetNumberOfTotalLanes(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Displayrelativepre-emphasis
voidGetRelativePreEmphasis(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Relativepre-emphasissettingmenucontrol
voidRelativePreEmphasisSettingMenuControl(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Setrelativepre-emphasis
voidSetRelativePreEmphasis(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display,intrelativePreEmphasis);

//Displayrelativepre-emphasis
voidGetRelativeVoltageSwing(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Relativevoltageswingsettingmenucontrol
voidRelativeVoltageSwingSettingMenuControl(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Setrelativepre-emphasis
voidSetRelativeVoltageSwing(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display,intrelativeVoltageSwing);

//Displaycurrentlinkprotectionstatue
voidGetEnabledLinkProtection(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServices2Ptr&displayService,constIADLXDisplayPtr&display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
IADLXDisplayServices2PtrdisplayServices1(displayService);

if(nullptr!=displayServices1)
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_FAILED(res))
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("Failedtogetthedisplaylist",0);
}
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayServices1,display);
}
}
}
else
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("Failedtogetthedisplayservices",0);
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pausetoseetheprintout
system("Pause");

return0;
}

voidShowSupportedHDMIQualityDetection(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display)
{
IADLXDisplayConnectivityExperiencePtrdisplayConnectivityExperience;
ADLX_RESULTres=displayService2->GetDisplayConnectivityExperience(display,&displayConnectivityExperience);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===GetHDMIqualitydetectionSupported==="<<std::endl;
adlx_boolsupported=false;
res=displayConnectivityExperience->IsSupportedHDMIQualityDetection(&supported);
std::cout<<"\tHDMIqualitydetectionissupportedonthedisplay:"<<(supported?"true":"false")<<",returncode:"<<res<<"(0meanssuccess)"<<std::endl;
}
}

