voidSetEnabledHDMIQualityDetection(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display,adlx_boolenableDisable)
{
IADLXDisplayConnectivityExperiencePtrdisplayConnectivityExperience;
ADLX_RESULTres=displayService2->GetDisplayConnectivityExperience(display,&displayConnectivityExperience);
if(ADLX_SUCCEEDED(res))
{
if(!enableDisable)
{
res=displayConnectivityExperience->SetEnabledHDMIQualityDetection(false);
std::cout<<"\tSetenabledtofalse,result:"<<res<<"(0meanssuccess)"<<std::endl;
}
else
{
res=displayConnectivityExperience->SetEnabledHDMIQualityDetection(true);
std::cout<<"\tSetenabledtotrue,result:"<<res<<"(0meanssuccess)"<<std::endl;
}
}
}

voidGetDPLinkRate(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display)
{
conststd::unordered_map<int,std::string>DPLinkRateMap=
{
{0,"DP_LINK_RATE_RBR"},
{1,"DP_LINK_RATE_2_16GBPS"},
{2,"DP_LINK_RATE_2_43GBPS"},
{3,"DP_LINK_RATE_HBR"},
{4,"DP_LINK_RATE_4_32GBPS"},
{5,"DP_LINK_RATE_HBR2"},
{6,"DP_LINK_RATE_HBR3"},
{7,"DP_LINK_RATE_UHBR10"},
{8,"DP_LINK_RATE_UHBR13D5"},
{9,"DP_LINK_RATE_UHBR20"}};

IADLXDisplayConnectivityExperiencePtrdisplayConnectivityExperience;
ADLX_RESULTres=displayService2->GetDisplayConnectivityExperience(display,&displayConnectivityExperience);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===GetDPlinkrate==="<<std::endl;
ADLX_DP_LINK_RATEDPLinkRate=ADLX_DP_LINK_RATE::DP_LINK_RATE_UNKNOWN;
res=displayConnectivityExperience->GetDPLinkRate(&DPLinkRate);
std::stringDPLinkRateStr=0!=DPLinkRateMap.count(DPLinkRate)?DPLinkRateMap.at(DPLinkRate):"DP_LINK_RATE_UNKNOWN";
std::cout<<"\tGetValue,result:"<<res<<"mode:"<<DPLinkRateStr.c_str()<<std::endl;
}
}

