voidMainMenu()
{
std::cout<<"\tChoosefromfollowingoptions"<<std::endl;

std::cout<<"\t->Press1todisplayHDMIqualitydetectionsupport"<<std::endl;
std::cout<<"\t->Press2todisplayDPlinksupport"<<std::endl;
std::cout<<"\t->Press3togetHDMIqualitydetectionstatus"<<std::endl;
std::cout<<"\t->Press4tosetHDMIqualitydetectiontoEnable"<<std::endl;
std::cout<<"\t->Press5tosetHDMIqualitydetectionstatustoDisable"<<std::endl;
std::cout<<"\t->Press6todisplayDPlinkrate"<<std::endl;
std::cout<<"\t->Press7todisplaynumberofactivelanes"<<std::endl;
std::cout<<"\t->Press8todisplaynumberoftotallanes"<<std::endl;
std::cout<<"\t->Press9todisplayrelativepre-emphasis"<<std::endl;
std::cout<<"\t->PressatoentertheRelativePre-emphasissettingsmenu"<<std::endl;
std::cout<<"\t->Pressbtodisplayrelativevoltageswing"<<std::endl;
std::cout<<"\t->PressctoentertheRelativeVoltageSwingsettingsmenu"<<std::endl;
std::cout<<"\t->Pressdtodisplayiflinkprotectionisenabled"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServices2Ptr&displayService2,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplaysupportedHDMIqualitydetection
case'1':
ShowSupportedHDMIQualityDetection(displayService2,display);
break;

//DisplayssupportedDPlink
case'2':
ShowSupportedDPLink(displayService2,display);
break;

//GetHDMIqualitydetectionenable
case'3':
GetEnabledHDMIQualityDetection(displayService2,display);
break;

//SetHDMIqualitydetectionenable
case'4':
SetEnabledHDMIQualityDetection(displayService2,display,true);
break;

//SetHDMIqualitydetectiondisable
case'5':
SetEnabledHDMIQualityDetection(displayService2,display,false);
break;

//DisplayDPlinkrate
case'6':
GetDPLinkRate(displayService2,display);
break;

//Displaynumberofactivelanes
case'7':
GetNumberOfActiveLanes(displayService2,display);
break;

//Displaynumberoftotlalanes
case'8':
GetNumberOfTotalLanes(displayService2,display);
break;

//Displayrelativepre-emphasis
case'9':
GetRelativePreEmphasis(displayService2,display);
break;

//Enterrelativepre-emphasissettingcontrol
case'a':
RelativePreEmphasisSettingMenuControl(displayService2,display);
break;

//Displayrelativevoltageswing
case'b':
GetRelativeVoltageSwing(displayService2,display);
break;

//Enterrelativevoltageswingsettingcontrol
case'c':
RelativeVoltageSwingSettingMenuControl(displayService2,display);
break;

//Displaylinkprotectionstatus
case'd':
GetEnabledLinkProtection(displayService2,display);
break;

case'm':
case'M':
MainMenu();
break;

default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("Pause");
returnretCode;
}
