#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"
#include<iostream>
#include<string>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestoryed.
//soweuseglobalvariablestoensurethevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//DisplaydisplayVariBrightsupport
voidShowDisplayVariBrightSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//DisplaycurrentdisplayVariBrightstate
voidGetVariBrightEnabled(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//SetdisplayVariBrightenable/disable
voidSetVariBrightEnabled(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,adlx_boolenableDisable);

//GetdisplayVariBrightcurrentstate
voidGetCurrentVariBright(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,intuserChoice);

//GetdisplayVariBrightcurrentstate
voidSetCurrentVariBright(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,intuserChoice);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService,display);
}
}
else
{
std::cout<<"Failedtogetthedisplaylist"<<std::endl;
}
}
else
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("Failedtogetthedisplayservices",0);
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pausetoseetheprintout
system("Pause");

return0;
}

voidShowDisplayVariBrightSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
IADLXDisplayVariBrightPtrdisplayVariBright;
ADLX_RESULTres=displayService->GetVariBright(display,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===GetVariBrightSupported==="<<std::endl;
adlx_boolsupported=false;
res=displayVariBright->IsSupported(&supported);
std::cout<<"\tVari-Brightissupportedonthedisplay:"<<(supported?"true":"false")<<",returncode:"<<res<<"(0meanssuccess)"<<std::endl;
}
}

voidGetVariBrightEnabled(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
IADLXDisplayVariBrightPtrdisplayVariBright;
ADLX_RESULTres=displayService->GetVariBright(display,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===GetVariBrightenabled==="<<std::endl;
adlx_boolenabled=false;
res=displayVariBright->IsEnabled(&enabled);
std::cout<<"\tGetEnabled,result:"<<res<<"enabled:"<<(enabled?"true":"false")<<std::endl;
}
}

voidSetVariBrightEnabled(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,adlx_boolenableDisable)
{
IADLXDisplayVariBrightPtrdisplayVariBright;
ADLX_RESULTres=displayService->GetVariBright(display,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
if(!enableDisable)
{
res=displayVariBright->SetEnabled(false);
std::cout<<"\tSetenabledtofalse,result:"<<res<<std::endl;
}
else
{
res=displayVariBright->SetEnabled(true);
std::cout<<"\tSetenabledtotrue,result:"<<res<<std::endl;
}
}
}

voidGetCurrentVariBright(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,intuserChoice)
{
IADLXDisplayVariBrightPtrdisplayVariBright;
ADLX_RESULTres=displayService->GetVariBright(display,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
adlx_boolapplied=false;
switch(userChoice)
{
case0:
res=displayVariBright->IsCurrentMaximizeBrightness(&applied);
std::cout<<"\tMaximizeBrightnesspresetisusedonthedisplay:"<<(applied?"Yes":"No")<<",returncode:"<<res<<"(0meanssuccess)"<<std::endl;
break;

case1:
res=displayVariBright->IsCurrentOptimizeBrightness(&applied);
std::cout<<"\tOptimizeBrightnesspresetisusedonthedisplay:"<<(applied?"Yes":"No")<<",returncode:"<<res<<"(0meanssuccess)"<<std::endl;
break;

case2:
res=displayVariBright->IsCurrentBalanced(&applied);
std::cout<<"\tBalancedpresetisusedonthedisplay:"<<(applied?"Yes":"No")<<",returncode:"<<res<<"(0meanssuccess)"<<std::endl;
break;

case3:
res=displayVariBright->IsCurrentOptimizeBattery(&applied);
std::cout<<"\tOptimizeBatterypresetisusedonthedisplay:"<<(applied?"Yes":"No")<<",returncode:"<<res<<"(0meanssuccess)"<<std::endl;
break;

case4:
res=displayVariBright->IsCurrentMaximizeBattery(&applied);
std::cout<<"\tMaximizeBatterypresetisusedonthedisplay:"<<(applied?"Yes":"No")<<",returncode:"<<res<<"(0meanssuccess)"<<std::endl;
break;
}
}
}

voidSetCurrentVariBright(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,intuserChoice)
{
IADLXDisplayVariBrightPtrdisplayVariBright;
ADLX_RESULTres=displayService->GetVariBright(display,&displayVariBright);
if(ADLX_SUCCEEDED(res))
{
switch(userChoice)
{
case0:
res=displayVariBright->SetMaximizeBrightness();
std::cout<<"\tSetMaximizeBrightness,result:"<<res<<std::endl;
break;

case1:
res=displayVariBright->SetOptimizeBrightness();
std::cout<<"\tSetOptimizeBrightness,result:"<<res<<std::endl;
break;

case2:
res=displayVariBright->SetBalanced();
std::cout<<"\tSetBalanced,result:"<<res<<std::endl;
break;
case3:
res=displayVariBright->SetOptimizeBattery();
std::cout<<"\tSetOptimizeBattery,result:"<<res<<std::endl;
break;

case4:
res=displayVariBright->SetMaximizeBattery();
std::cout<<"\tSetMaximizeBattery,result:"<<res<<std::endl;
break;
}
}
}

voidMainMenu()
{
std::cout<<"\tChoosefromfollowingoptions"<<std::endl;

std::cout<<"\t->Press1todisplayVariBrightsupport"<<std::endl;
std::cout<<"\t->Press2togetVariBrightenablestate"<<std::endl;
std::cout<<"\t->Press3tosetVariBrightenable"<<std::endl;
std::cout<<"\t->Press4tosetVariBrightdisable"<<std::endl;
std::cout<<"\t->Press5tocheckifVari-BrightmodeisMaximizedBrightnesspresetonadisplay"<<std::endl;
std::cout<<"\t->Press6tocheckifVari-BrightmodeisOptimizedBrightnesspresetonadisplay"<<std::endl;
std::cout<<"\t->Press7tocheckifVari-BrightmodeisBalancedpresetonadisplay"<<std::endl;
std::cout<<"\t->Press8tocheckifVari-BrightmodeisOptimizedBatterypresetonadisplay"<<std::endl;
std::cout<<"\t->Press9tocheckifVari-BrightmodeisMaximizedBatterypresetonadisplay"<<std::endl;
std::cout<<"\t->PressatosettheVari-BrightmodetoMaximizedBrightnesspresetonadisplay"<<std::endl;
std::cout<<"\t->PressbtosettheVari-BrightmodetoOptimizedBrightnesspresetonadisplay"<<std::endl;
std::cout<<"\t->PressctosettheVari-BrightmodetoBalancedpresetonadisplay"<<std::endl;
std::cout<<"\t->PressdtosettheVari-BrightmodetoOptimizedBatterypresetonadisplay"<<std::endl;
std::cout<<"\t->PressetosettheVari-BrightmodetoMaximizedBatterypresetonadisplay"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplaydisplayVariBrightsupport
case'1':
ShowDisplayVariBrightSupport(displayService,display);
break;

//DisplaycurrentdisplayVariBrightstate
case'2':
GetVariBrightEnabled(displayService,display);
break;

//SetdisplayVariBrightenable
case'3':
SetVariBrightEnabled(displayService,display,true);
break;

//SetdisplayVariBrightenable
case'4':
SetVariBrightEnabled(displayService,display,false);
break;

case'5':
case'6':
case'7':
case'8':
case'9':
GetCurrentVariBright(displayService,display,num-'5');
break;

case'a':
case'b':
case'c':
case'd':
case'e':
SetCurrentVariBright(displayService,display,num-'a');
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;

default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("Pause");
returnretCode;
}
