#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"
#include<iostream>
#include<string>
#include<unordered_map>
#include<algorithm>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//soweuseglobalvariablestoensurethevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//DisplaydisplayPixelFormatsupport
voidShowDisplayPixelFormatSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//DisplaycurrentdisplayPixelFormatstate
voidGetPixelFormatState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//SetdisplayPixelFormat
voidSetPixelFormatState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey);

//Getsupportstateforeachpixelformat
voidGetSupportStateForEachPixelFormat(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService,display);
}
}
else
{
std::cout<<"Failedtogetthedisplaylist"<<std::endl;
}
}
else
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("Failedtogetthedisplayservices",0);
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXresult:"<<res<<std::endl;

//Pausetoseetheprintout
system("Pause");

return0;
}

voidShowDisplayPixelFormatSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
IADLXDisplayPixelFormatPtrdisplayPixelFormat;
ADLX_RESULTres=displayService->GetPixelFormat(display,&displayPixelFormat);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Getpixelformatsupported==="<<std::endl;
adlx_boolsupported=false;
res=displayPixelFormat->IsSupported(&supported);
std::cout<<"\tPixelformatissupportedonthedisplay:"<<supported<<",returncode:"<<res<<"(0meanssuccess)\n"<<std::endl;
}
}

voidGetPixelFormatState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
conststd::unordered_map<int,std::string>PixelFormatMap={{0,"FORMAT_UNKNOWN"},{1,"RGB_444_FULL"},{2,"YCBCR_444"},{3,"YCBCR_422"},{4,"RGB_444_LIMITED"}};
IADLXDisplayPixelFormatPtrdisplayPixelFormat;
ADLX_RESULTres=displayService->GetPixelFormat(display,&displayPixelFormat);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Getpixelformatenabled==="<<std::endl;
ADLX_PIXEL_FORMATpf=FORMAT_UNKNOWN;
res=displayPixelFormat->GetValue(&pf);
std::stringpfStr="FORMAT_UNKNOWN";
autosearchIter=PixelFormatMap.find(pf);
if(searchIter!=PixelFormatMap.end())
{
pfStr=searchIter->second;
}
std::cout<<"\tGetValue,res"<<res<<"pixelformat:"<<pfStr.c_str()<<std::endl;
}
}

voidSetPixelFormatState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey)
{
IADLXDisplayPixelFormatPtrdisplayPixelFormat;
ADLX_RESULTres=displayService->GetPixelFormat(display,&displayPixelFormat);
if(ADLX_SUCCEEDED(res))
{
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//SetpixelformattoRGB_444_FULL
case0:
std::cout<<"===SetpixelformattoRGB_444_FULL"<<std::endl;
res=displayPixelFormat->SetValue(RGB_444_FULL);
break;
//SetpixelformattoYCBCR_444
case1:
std::cout<<"===SetpixelformattoYCBCR_444"<<std::endl;
res=displayPixelFormat->SetValue(YCBCR_444);
break;
//SetpixelformattoYCBCR_422
case2:
std::cout<<"===SetpixelformattoYCBCR_422"<<std::endl;
res=displayPixelFormat->SetValue(YCBCR_422);
break;
//SetpixelformattoRGB_444_LIMITED
case3:
std::cout<<"===SetpixelformattoRGB_444_LIMITED"<<std::endl;
res=displayPixelFormat->SetValue(RGB_444_LIMITED);
break;
default:
break;
}
std::cout<<"\tReturncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}
}

//Getsupportstateforeachpixelformat
voidGetSupportStateForEachPixelFormat(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
IADLXDisplayPixelFormatPtrdisplayPixelFormat;
ADLX_RESULTres=displayService->GetPixelFormat(display,&displayPixelFormat);
if(ADLX_SUCCEEDED(res)&&displayPixelFormat)
{
std::cout<<"===Getpixelformatlist==="<<std::endl;
adlx_boolsupport=false;

res=displayPixelFormat->IsSupportedRGB444Full(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIssupportRGB4:4:4PCStandard(FullRGB):"<<support<<std::endl;
res=displayPixelFormat->IsSupportedYCbCr444(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIssupportYCbCr4:4:4:"<<support<<std::endl;
res=displayPixelFormat->IsSupportedYCbCr422(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIssupportYCbCr4:2:2:"<<support<<std::endl;
res=displayPixelFormat->IsSupportedRGB444Limited(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIssupportRGB4:4:4Studio(LimitedRGB):"<<support<<std::endl;
res=displayPixelFormat->IsSupportedYCbCr420(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIssupportYCbCr4:2:0:"<<support<<std::endl;

conststd::unordered_map<ADLX_PIXEL_FORMAT,std::string>PixelFormatMap{
{RGB_444_FULL,"RGB4:4:4PCStandard(FullRGB)"},
{YCBCR_444,"YCbCr4:4:4"},
{YCBCR_422,"YCbCr4:2:2"},
{RGB_444_LIMITED,"RGB4:4:4Studio(LimitedRGB)"},
{YCBCR_420,"YCbCr4:2:0"}
};
for_each(PixelFormatMap.begin(),PixelFormatMap.end(),[&](autopixelFormat){
res=displayPixelFormat->IsSupportedPixelFormat(pixelFormat.first,&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIssupport"<<pixelFormat.second<<":"<<support<<std::endl;
});
}
}

voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1todisplayPixelFormatsupport"<<std::endl;

std::cout<<"\t->Press2togetPixelFormatstate"<<std::endl;

std::cout<<"\t->Press3tosetcurrentpixelformattoRGB_444_FULL"<<std::endl;
std::cout<<"\t->Press4tosetcurrentpixelformattoYCBCR_444"<<std::endl;
std::cout<<"\t->Press5tosetcurrentpixelformattoYCBCR_422"<<std::endl;
std::cout<<"\t->Press6tosetcurrentpixelformattoRGB_444_LIMITED"<<std::endl;

std::cout<<"\t->Press7togetsupportstateforeachpixelformat"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplaydisplayPixelFormatsupport
case'1':
ShowDisplayPixelFormatSupport(displayService,display);
break;

//DisplaycurrentdisplayPixelFormatstate
case'2':
GetPixelFormatState(displayService,display);
break;

//SetdisplayPixelFormat
case'3':
case'4':
case'5':
case'6':
SetPixelFormatState(displayService,display,num-'3');
break;

//GetdisplayPixelFormatsupportlist
case'7':
GetSupportStateForEachPixelFormat(displayService,display);
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("Pause");
returnretCode;
}
