#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/ISmartAccessMemory.h"
#include"SDK/Include/IGPUTuning1.h"
#include<iostream>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

staticHANDLESAMEvent=nullptr;

classGPUTuningChangedListener:publicIADLXGPUTuningChangedListener
{
public:

boolADLX_STD_CALLOnGPUTuningChanged(IADLXGPUTuningChangedEvent*pGPUTuningChangedEvent)
{
IADLXGPUTuningChangedEvent1PtrpGPUTuningChangedEvent1=IADLXGPUTuningChangedEvent1Ptr(pGPUTuningChangedEvent);
adlx_boolSAMChange=pGPUTuningChangedEvent1->IsSmartAccessMemoryChanged();
if(SAMChange)
{
adlx_boolenabled=false,completed=false;
pGPUTuningChangedEvent1->GetSmartAccessMemoryStatus(&enabled,&completed);
}

SetEvent(SAMEvent);
returntrue;
}
};

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//FindthefirstGPUindexthatsupportsAMDSmartAccessMemory.Return-1ifallGPUsarenotsupported
adlx_uintGetGPUIndexSupportSAM(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus);

//DisplayAMDSmartAccessMemorysupportstatusonaGPU
voidShowSmartAccessMemorySupport(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus);

//DisplayAMDSmartAccessMemoryenabledstatus
voidShowSmartAccessMemoryState(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus);

//SetAMDSmartAccessMemorystate
voidSetSmartAccessMemoryState(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus);

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();

if(ADLX_SUCCEEDED(res))
{
IADLXGPUTuningServicesPtrgpuTuningService;
res=g_ADLXHelp.GetSystemServices()->GetGPUTuningServices(&gpuTuningService);
if(ADLX_SUCCEEDED(res))
{
//GetgpuTuningService1viagpuTuningService::QueryInterface()
IADLXGPUTuningServices1PtrgpuTuningService1;
res=gpuTuningService->QueryInterface(gpuTuningService1->IID(),reinterpret_cast<void**>(&gpuTuningService1));
if(ADLX_SUCCEEDED(res))
{
IADLXGPUTuningChangedHandlingPtrgpuTuningHandling;
res=gpuTuningService1->GetGPUTuningChangedHandling(&gpuTuningHandling);
if(ADLX_SUCCEEDED(res))
{
//Addlistener
SAMEvent=CreateEvent(NULL,FALSE,FALSE,NULL);
staticGPUTuningChangedListenerlistener;
ADLX_RESULTeventRes=gpuTuningHandling->AddGPUTuningEventListener(&listener);

//Menu
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();

//Getandexecutethechoice
MenuControl(gpuTuningService1,gpus);
}
else
std::cout<<"FailedtogetGPUlist"<<std::endl;

//Removelistener
if(ADLX_SUCCEEDED(eventRes))
eventRes=gpuTuningHandling->RemoveGPUTuningEventListener(&listener);
}
else
std::cout<<"FailedtogetGPUTuningChangedHandling"<<std::endl;
}
else
std::cout<<"FailedtogetgpuTuningService1"<<std::endl;
}
else
std::cout<<"FailedtogetgpuTuningService"<<std::endl;
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;

std::cout<<"\t->Press1todisplayAMDSmartAccessMemorysupportedGPUs"<<std::endl;
std::cout<<"\t->Press2todisplayAMDSmartAccessMemoryenabledstatus"<<std::endl;
std::cout<<"\t->Press3tosettheAMDSmartAccessMemorystate"<<std::endl;
std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayAMDSmartAccessMemorysupportstatusonaGPU
case'1':
ShowSmartAccessMemorySupport(gpuTuningService1,gpus);
break;

//DisplayAMDSmartAccessMemoryenabledstatus
case'2':
ShowSmartAccessMemoryState(gpuTuningService1,gpus);
break;

//SetAMDSmartAccessMemorystate
case'3':
SetSmartAccessMemoryState(gpuTuningService1,gpus);
break;

//Displaymenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//FindthefirstGPUindexthatsupportsAMDSmartAccessMemory.Return-1ifallGPUsarenotsupported
adlx_uintGetGPUIndexSupportSAM(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus)
{
adlx_uintsupportedIndex=-1;
IADLXGPUPtroneGPU;
IADLXSmartAccessMemoryPtrsmartAccessMemory;
for(autoindex=gpus->Begin();index!=gpus->End();++index)
{
ADLX_RESULTres=gpus->At(index,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
//GettheAMDSmartAccessMemoryinterface
res=gpuTuningService1->GetSmartAccessMemory(oneGPU,&smartAccessMemory);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=false;
ADLX_RESULTres=smartAccessMemory->IsSupported(&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
supportedIndex=index;
break;
}
}
}
}
returnsupportedIndex;
}

//DisplayAMDSmartAccessMemorysupportstatusonaGPU
voidShowSmartAccessMemorySupport(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus)
{
autoindex=GetGPUIndexSupportSAM(gpuTuningService1,gpus);
if(index==-1)
std::cout<<"AllGPUsdoesn'tsupportAMDSmartAccessMemory"<<std::endl;
else
std::cout<<"The"<<(index+1)<<"thGPUsupportAMDSmartAccessMemory"<<std::endl;
}

//DisplayAMDSmartAccessMemoryenabledstatus
voidShowSmartAccessMemoryState(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus)
{
autoindex=GetGPUIndexSupportSAM(gpuTuningService1,gpus);
if(index==-1)
std::cout<<"AllGPUsdoesn'tsupportAMDSmartAccessMemory"<<std::endl;
else
{
IADLXGPUPtroneGPU;
ADLX_RESULTres=gpus->At(index,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
IADLXSmartAccessMemoryPtrsmartAccessMemory;
res=gpuTuningService1->GetSmartAccessMemory(oneGPU,&smartAccessMemory);
if(ADLX_SUCCEEDED(res))
{
adlx_boolenabled=false;
res=smartAccessMemory->IsEnabled(&enabled);
if(ADLX_SUCCEEDED(res))
std::cout<<"TheAMDSmartAccessMemoryis"<<(enabled?"enabled":"disabled")<<"onthe"<<(index+1)<<"thGPU"<<std::endl;
else
std::cout<<"CallIsEnabled()failed"<<std::endl;
}
else
std::cout<<"FailedtogetsmartAccessMemory"<<std::endl;
}
else
std::cout<<"FailedtogettheGPU"<<std::endl;
}
}

//SetAMDSmartAccessMemorystate
voidSetSmartAccessMemoryState(IADLXGPUTuningServices1PtrgpuTuningService1,IADLXGPUListPtrgpus)
{
autoindex=GetGPUIndexSupportSAM(gpuTuningService1,gpus);
if(index==-1)
std::cout<<"AllGPUsdoesn'tsupportAMDSmartAccessMemory"<<std::endl;
else
{
IADLXGPUPtroneGPU;
ADLX_RESULTres=gpus->At(index,&oneGPU);
if(ADLX_SUCCEEDED(res))
{
IADLXSmartAccessMemoryPtrsmartAccessMemory;
res=gpuTuningService1->GetSmartAccessMemory(oneGPU,&smartAccessMemory);
if(ADLX_SUCCEEDED(res))
{
adlx_boolenabled=false;
res=smartAccessMemory->IsEnabled(&enabled);
if(ADLX_SUCCEEDED(res))
std::cout<<"CurrentlyAMDSmartAccessMemoryis"<<(enabled?"enabled":"disabled")<<"on"<<(index+1)<<"thGPU"<<std::endl;
else
std::cout<<"CallIsEnabled()failed"<<std::endl;
res=smartAccessMemory->SetEnabled(!enabled);
if(ADLX_SUCCEEDED(res))
std::cout<<"SetAMDSmartAccessMemoryto"<<(!enabled?"enabled":"disabled")<<"for"<<(index+1)<<"thGPU"<<std::endl;
else
std::cout<<"CallSetEnabled()failed"<<std::endl;

//FirsteventreceivedquicklybeforeSAMstart
WaitForSingleObject(SAMEvent,2000);

//Whenreceivethefirstevent,avoidcallinganyotherADLXmethod,andifanyUIapplicationisrunning,wemustcloseittoavoidcrashing
//Close(application)......

//SecondeventreceivedafterSAMcompleted,themaximumconsumingtimelessthan20seconds.
WaitForSingleObject(SAMEvent,20000);

//NowSAMcompleted,wecanrestarttheUIapplication,andcontinuetocallADLXfunction
//Start(application)......

res=smartAccessMemory->IsEnabled(&enabled);
if(ADLX_SUCCEEDED(res))
std::cout<<"Aftersetting,AMDSmartAccessMemoryis"<<(enabled?"enabled":"disabled")<<"on"<<(index+1)<<"thGPU"<<std::endl;
else
std::cout<<"CallIsEnabled()failed"<<std::endl;
}
else
std::cout<<"FailedtogetsmartAccessMemory"<<std::endl;
}
else
std::cout<<"FailedtogettheGPU"<<std::endl;
}
}
