//Helperfunctions

//Algorigthmtogenerateavaluewhichrangesfrom0to0xFFFF
inlinedoubleInputvalue(intindex,intbitDepth,intnumOfPoints)
{
returnfmin(static_cast<float>(index*pow(2,bitDepth)/static_cast<float>(numOfPoints-1)),pow(2,bitDepth)-1);
}

//The3DLUTarethreeaxesofa3-dimensionalcube
//Generate3DLutdatabitwise:17*17*17triplets/latticeofRGBvalues.
//EachtripletcontainsthecomponentofRGBvalues(16-bitdepth)from0to0xFFFF.
adlx_boolGenerateUser3DLUT_BW(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,adlx_intnumOfPoints,ADLX_3DLUT_Data*user3DLUT)
{
adlx_boolret=true;

constintbitDepth=16;

IADLXDisplay3DLUTPtrdisplay3DLUT;
ADLX_RESULTres=displayService->Get3DLUT(display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
ADLX_UINT16_RGBrgbCoordinate={0,0,0};
adlx_intindex=0;

for(intr=0;r<numOfPoints;r++)
{
for(intg=0;g<numOfPoints;g++)
{
for(intb=0;b<numOfPoints;b++)
{
rgbCoordinate.red=r;
rgbCoordinate.green=g;
rgbCoordinate.blue=b;

//Recalculate3DLUTbasedonthe"Inputvalue"whichrangesfrom0to0xFFFF.Therecalculatedvaluefallsinthesamerangeof0to0xFFFFtobemore"random".
doubletemp=0.25*Inputvalue(r,bitDepth,numOfPoints)+0.625*Inputvalue(g,bitDepth,numOfPoints)+0.125*Inputvalue(b,bitDepth,numOfPoints);

//Gettheindexinthe3DLUTbuffercorrespondingtotheRGBtriplet.
res=display3DLUT->GetUser3DLUTIndex(numOfPoints,&rgbCoordinate,&index);

if(ADLX_SUCCEEDED(res))
{
adlx_uint16tValue=static_cast<adlx_uint16>(temp);
user3DLUT->data[index].red=tValue;
user3DLUT->data[index].green=tValue;
user3DLUT->data[index].blue=tValue;
}
else
{
ret=false;
gotoEXIT;
}
}
}
}
}

EXIT:
returnret;
}

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}
}
else
{
std::cout<<"FailedtogetDisplaylist"<<std::endl;
}
}
else
{
std::cout<<"FailedtogetDisplayServices"<<std::endl;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Displaydisplay3DLUTsupport
voidShowDisplay3DLUTSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//Getdisplay3DLUT
IADLXDisplay3DLUTPtrdisplay3DLUT;
ADLX_RESULTres=displayService->Get3DLUT(display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===3DLUTsupportedstatus==="<<std::endl;
adlx_boolsupported;
ADLX_RESULTres=display3DLUT->IsSupportedSCE(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedSCE:"<<supported<<std::endl;
res=display3DLUT->IsSupportedSCEVividGaming(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedSCE_VividGaming:"<<supported<<std::endl;
res=display3DLUT->IsSupportedSCEDynamicContrast(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedSCEDynamicContrast:"<<supported<<std::endl;
res=display3DLUT->IsSupportedUser3DLUT(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedUser3DLUT:"<<supported<<std::endl;
}
}

//Getanddisplaycurrentdisplay3DLUTstate
voidGet3DLUTState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//Getdisplay3DLUT
IADLXDisplay3DLUTPtrdisplay3DLUT;
ADLX_RESULTres=displayService->Get3DLUT(display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Get3DLUT==="<<std::endl;
adlx_boolapplied=false;
res=display3DLUT->IsCurrentSCEDisabled(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentSCEDisabled:"<<applied<<std::endl;
res=display3DLUT->IsCurrentSCEVividGaming(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentSCEVividGaming:"<<applied<<std::endl;
res=display3DLUT->IsCurrentSCEDynamicContrast(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentSCEDynamicContrast:"<<applied<<std::endl;
if(applied)
{
ADLX_IntRangerange;
res=display3DLUT->GetSCEDynamicContrastRange(&range);
std::cout<<"\t\tDynamicContrastrangeis:["<<range.minValue<<","<<range.maxValue<<"]"<<std::endl;
adlx_intcontrast;
res=display3DLUT->GetSCEDynamicContrast(&contrast);
std::cout<<"\t\tDynamicContrastis:"<<contrast<<std::endl;
}
}
}

//Setdisplay3DLUT
voidSet3DLUTState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey)
{
//Getdisplay3DLUT
IADLXDisplay3DLUTPtrdisplay3DLUT;
ADLX_RESULTres=displayService->Get3DLUT(display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Set3DLUT==="<<std::endl;
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//SetSCEdisabled
case0:
res=display3DLUT->SetSCEDisabled();
break;

//SetSCEVividGaming
case1:
res=display3DLUT->SetSCEVividGaming();
break;
//SetSCEDynamicContrast
case2:
{
ADLX_IntRangerange;
display3DLUT->GetSCEDynamicContrastRange(&range);
adlx_intcurContrast;
display3DLUT->GetSCEDynamicContrast(&curContrast);
adlx_intcontrast;
if(curContrast!=range.minValue)
{
contrast=range.minValue;
res=display3DLUT->SetSCEDynamicContrast(contrast);
std::cout<<"\tUseminimumDynamicContrast"<<std::endl;
}
else
{
contrast=range.maxValue;
res=display3DLUT->SetSCEDynamicContrast(contrast);
std::cout<<"\tUsemaximumDynamicContrast"<<std::endl;
}
}
break;
default:
break;
}
std::cout<<"\tReturncodeis:"<<res<<"(0meansSuccess)"<<std::endl;
}
}

