//GetorSetSDRuser3DLUT
voidDemoSDRUser3DLUT(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//Getdisplay3DLUT
IADLXDisplay3DLUTPtrdisplay3DLUT;
ADLX_RESULTres=displayService->Get3DLUT(display,&display3DLUT);
if(ADLX_SUCCEEDED(res))
{
ADLX_3DLUT_TRANSFER_FUNCTIONtf;
ADLX_3DLUT_COLORSPACEcs;
ADLX_3DLUT_Datadata;
adlx_intnumOfPoints=0;

//Generatenew3DLUTdata
if(GenerateUser3DLUT_BW(displayService,display,DEFAULT_NUM_OF_POINTS,&data))
{
tf=TF_SRGB;
cs=CS_SRGB;

std::cout<<"===SetSDRUser3DLUT==="<<std::endl;
res=display3DLUT->SetSDRUser3DLUT(tf,cs,DEFAULT_NUM_OF_POINTS,&data);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===SetSDRUser3DLUTsucceed==="<<std::endl;
}
else
std::cout<<"===SetSDRUser3DLUTfailed==="<<std::endl;

std::cout<<"===GetSDRUser3DLUT==="<<std::endl;
res=display3DLUT->GetSDRUser3DLUT(&tf,&cs,&numOfPoints,&data);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===GetSDRUser3DLUTsucceed==="<<std::endl;
}
else
std::cout<<"===GetSDRUser3DLUTfailed==="<<std::endl;

//Clear3DLUT
res=display3DLUT->ClearUser3DLUT();
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Clearsucceed==="<<std::endl;
}
else
std::cout<<"===Clearfailed==="<<std::endl;
}
}
}

