std::cout<<"\t->Press6togetuser3DLUTsupport"<<std::endl;
std::cout<<"\t->Press7toclearuser3DLUT"<<std::endl;
std::cout<<"\t->Press8toshowhowtoset/getSDRuser3DLUT"<<std::endl;
std::cout<<"\t->Press9toshowhowtoset/getHDRuser3DLUT"<<std::endl;
std::cout<<"\t->Pressatoshowhowtoset/getalluser3DLUT"<<std::endl;
