#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IDisplaySettings.h"
#include"SDK/Include/IDisplays.h"
#include"SDK/Include/IDisplays1.h"
#include<iostream>
#include<string>
#include<thread>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestoryed.
//soweuseglobalvariablestoensurethevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//threadquitevent
staticHANDLEquitEvent=nullptr;

//threadquitflag
staticstd::atomic<bool>g_quit=false;

//threadfortimeout
staticstd::thread*g_thread=nullptr;

//threadtounblankdisplay
staticvoidThreadUnBlankDisplay(IADLXDisplayBlankingPtrdisplayBlanking);

//Displaydisplayblankingsupport
voidShowDisplayBlankingSupport(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display);

//Displaycurrentdisplayblankingstate
voidGetDisplayBlankingState(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display);

//Setdisplayblankedorunblaned
voidSetDisplayBlankingState(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display,constintkey);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display);

//Selectdisplay
IADLXDisplayPtrSelectDisplay(constIADLXDisplayListPtr&displays);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
quitEvent=CreateEvent(NULL,TRUE,FALSE,NULL);

//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
//Converttodisplayservice1
IADLXDisplayServices1PtrdisplayService1(displayService);
if(ADLX_SUCCEEDED(res)&&displayService1)
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService1->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Selectthedisplaytoconfig
IADLXDisplayPtrdisplay=SelectDisplay(displayList);
if(display)
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService1,display);
}
}
else
{
std::cout<<"FailedtogettheDisplayslist"<<std::endl;
}
}
else
{
std::cout<<"Failedtogetthedisplayservices1"<<std::endl;
}

//quitthread
if(g_thread&&g_thread->joinable())
{
SetEvent(quitEvent);
g_quit=true;
g_thread->join();
deleteg_thread;
g_thread=nullptr;
}
CloseHandle(quitEvent);
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("Pause");

return0;
}

voidShowDisplayBlankingSupport(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display)
{
IADLXDisplayBlankingPtrdisplayBlanking;
ADLX_RESULTres=displayService1->GetDisplayBlanking(display,&displayBlanking);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Getdisplayblankingsupported==="<<std::endl;
adlx_boolsupported=false;
res=displayBlanking->IsSupported(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupported,result:"<<res<<"supported:"<<(supported?"true":"false")<<std::endl;
}
}

voidGetDisplayBlankingState(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display)
{
IADLXDisplayBlankingPtrdisplayBlanking;
ADLX_RESULTres=displayService1->GetDisplayBlanking(display,&displayBlanking);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Getdisplayblankingstate==="<<std::endl;
adlx_boolblanked=false;
res=displayBlanking->IsCurrentBlanked(&blanked);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentBlanked:"<<(blanked?"true":"false")<<std::endl;
adlx_boolunBlanked=false;
res=displayBlanking->IsCurrentUnblanked(&unBlanked);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentUnblanked:"<<(unBlanked?"true":"false")<<std::endl;
}
}

voidSetDisplayBlankingState(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display,constintkey)
{
IADLXDisplayBlankingPtrdisplayBlanking;
ADLX_RESULTres=displayService1->GetDisplayBlanking(display,&displayBlanking);
if(ADLX_SUCCEEDED(res))
{
ADLX_RESULTres=ADLX_FAIL;
switch(key)
{
//Setdisplayblanked
case0:
if(g_thread&&g_thread->joinable())
{
SetEvent(quitEvent);
g_quit=true;
g_thread->join();
deleteg_thread;
g_thread=nullptr;
}
std::cout<<"===Setdisplayblanked==="<<std::endl;
res=displayBlanking->SetBlanked();
break;

//Setdisplayunblanked
case1:
std::cout<<"===SetdisplayunBlanked==="<<std::endl;
res=displayBlanking->SetUnblanked();
break;

//Setdisplayblankedandrestoreafter20sec
case2:
if(g_thread&&g_thread->joinable())
{
SetEvent(quitEvent);
g_quit=true;
g_thread->join();
deleteg_thread;
g_thread=nullptr;
}
std::cout<<"===Setdisplayblankedfor20seconds==="<<std::endl;
res=displayBlanking->SetBlanked();
g_thread=newstd::thread(ThreadUnBlankDisplay,displayBlanking);
g_quit=false;
break;
default:
break;
}
std::cout<<"\tReturncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}
}

voidThreadUnBlankDisplay(IADLXDisplayBlankingPtrdisplayBlanking)
{
boolopt=false;
while(!g_quit)
{
DWORDr=WaitForSingleObject(quitEvent,20000);
//quitthethread
if(r==WAIT_OBJECT_0)
{
ResetEvent(quitEvent);
std::cout<<"\tThreadquit."<<std::endl;
break;
}
//Restoredisplayafter20seconds
if(r==WAIT_TIMEOUT&&displayBlanking&&!opt)
{
ADLX_RESULTres=displayBlanking->SetUnblanked();
std::cout<<"\tRestoredisplayafter20seconds,returncodeis:"<<res<<"(0meanssuccess)."<<std::endl;
opt=true;
}
}
}

voidMainMenu()
{
std::cout<<"\tChoosefromfollowingoptions"<<std::endl;

std::cout<<"\t->Press1toshowdisplayblankingsupport"<<std::endl;

std::cout<<"\t->Press2togetdisplayblankingstate"<<std::endl;

std::cout<<"\t->Press3tosetdisplayblanked"<<std::endl;
std::cout<<"\t->Press4tosetdisplayunblanked"<<std::endl;
std::cout<<"\t->Press5tosetdisplayblankedfor20seconds"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

IADLXDisplayPtrSelectDisplay(constIADLXDisplayListPtr&displays)
{
adlx_uintlen=displays->Size();
if(len==0)
returnnullptr;

IADLXDisplayPtrdisplay;
if(len<2)
displays->At(0,&display);
else
{
std::cout<<"\tYoucanselectthedisplaybythekey(from1to"<<len<<")."<<std::endl;
for(adlx_uintit=0;it<len;it++)
{
displays->At(it,&display);
constchar*dispName;
display->Name(&dispName);
std::cout<<"\t->Press"<<it+1<<"toselect"<<dispName<<"."<<std::endl;
}
intnum=0;
num=getchar()-48;
if(num<1||num>len)
std::cout<<"\tInvalidkey.Thefirstdisplaywillbeused."<<std::endl;
else
displays->At(num-1,&display);
}
returndisplay;
}
//Displayactioncontrol
voidMenuControl(constIADLXDisplayServices1Ptr&displayService1,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//Displaydisplayblankingsupport
case'1':
ShowDisplayBlankingSupport(displayService1,display);
break;

//Displaycurrentdisplayblankingstate
case'2':
GetDisplayBlankingState(displayService1,display);
break;

//Setdisplayblankingorunblanking
case'3':
case'4':
case'5':
SetDisplayBlankingState(displayService1,display,num-'3');
break;

//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("Pause");
returnretCode;
}
