intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(displayService,display);
}
}
else
{
std::cout<<"Failedtogetthedisplaylist"<<std::endl;
}
}
else
{
//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;
returnWaitAndExit("FailedtogettheDisplayServices",0);
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//Printgammaramp
staticvoidShowGammaRamp(constADLX_GammaRamp&gammaRamp)
{
for(intj=0;j<3;j++)
{
printf("\nGetGammaRamp[%c]:\n",(j==0)?'R':((j==1)?'G':(j==2)?'B'
:'E'));
for(inti=0;i<256;i++)
{
printf("%05d",gammaRamp.gamma[i+j*256]);
if(i%10==9)
printf("\n");
}
}
printf("\n");
}

//Displaygammasupportstatus
voidShowDisplayGammaSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//Getdisplaygamma
IADLXDisplayGammaPtrdisplayGamma;
ADLX_RESULTres=displayService->GetGamma(display,&displayGamma);
adlx_boolsupport=false;
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Re-Gammasupportedstatus==="<<std::endl;
res=displayGamma->IsSupportedReGammaSRGB(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedReGammaSRGB:"<<support<<std::endl;
res=displayGamma->IsSupportedReGammaBT709(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedReGammaBT709:"<<support<<std::endl;
res=displayGamma->IsSupportedReGammaPQ(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedReGammaPQ:"<<support<<std::endl;
res=displayGamma->IsSupportedReGammaPQ2084Interim(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedReGammaPQ2084Interim:"<<support<<std::endl;
res=displayGamma->IsSupportedReGamma36(&support);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsSupportedReGamma36:"<<support<<std::endl;
}
}

//Getcurrentgammastate
voidGetCurrentGammaState(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//Getdisplaygamma
IADLXDisplayGammaPtrdisplayGamma;
ADLX_RESULTres=displayService->GetGamma(display,&displayGamma);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Currentgammastate==="<<std::endl;
adlx_boolapplied=false;
ADLX_RESULTres=ADLX_FAIL;
ADLX_GammaRampramp;
ADLX_RegammaCoeffcoeff;

res=displayGamma->IsCurrentReGammaSRGB(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentReGammaSRGB"<<applied<<std::endl;
res=displayGamma->IsCurrentReGammaBT709(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentReGammaBT709"<<applied<<std::endl;
res=displayGamma->IsCurrentReGammaPQ(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentReGammaPQ"<<applied<<std::endl;
res=displayGamma->IsCurrentReGammaPQ2084Interim(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentReGammaPQ2084Interim"<<applied<<std::endl;
res=displayGamma->IsCurrentReGamma36(&applied);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentReGamma36"<<applied<<std::endl;

adlx_boolcurCoeff;
res=displayGamma->IsCurrentRegammaCoefficient(&curCoeff);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsCurrentRegammaCoefficient"<<curCoeff<<std::endl;

adlx_boolreGammaRamp=false;
adlx_booldeGammaRamp=false;
res=displayGamma->IsCurrentReGammaRamp(&reGammaRamp);
res=displayGamma->IsCurrentDeGammaRamp(&deGammaRamp);
if(reGammaRamp)
{
displayGamma->GetGammaRamp(&ramp);
std::cout<<"\tcurrentreGammaRamp"<<std::endl;
ShowGammaRamp(ramp);
}
elseif(deGammaRamp)
{
displayGamma->GetGammaRamp(&ramp);
std::cout<<"\tcurrentdeGammaRamp"<<std::endl;
ShowGammaRamp(ramp);
}
elseif(curCoeff)
{
displayGamma->GetGammaCoefficient(&coeff);
std::cout<<"\tcurrentcoefficient"<<std::endl;
std::cout<<"\tGetGammaCoefficient:\n"
<<"\tCoefficientA0"<<coeff.coefficientA0<<","
<<"CoefficientA1"<<coeff.coefficientA1<<","
<<"CoefficientA2"<<coeff.coefficientA2<<","
<<"CoefficientA3"<<coeff.coefficientA3<<","
<<"Gamma"<<coeff.gamma<<std::endl;
}
}
}

//Setgammabycoefficient;ifreturnvalueis0,gammacoefficientissuccessfullyset.Elseanerroroccured.
ADLX_RESULTSetGammaWithCustomCoeffs(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//Getdisplaygamma
IADLXDisplayGammaPtrdisplayGamma;
ADLX_RESULTres=displayService->GetGamma(display,&displayGamma);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"\t\tPleaseenterfiveintegernumbers,separatedbyspaces.(eg:313081292055552400)"<<std::endl;

ADLX_RESULTres=ADLX_OK;
ADLX_RegammaCoeffcoeff;
intuserKey=0;
std::vector<int>coeffs;
while(std::cin>>userKey)
{
coeffs.push_back(userKey);
if(coeffs.size()>4)
break;
}
std::cin.clear();
std::cin.ignore(INT_MAX,'\n');

if(coeffs.size()>4)
{
coeff.coefficientA0=coeffs[0];//31308;
coeff.coefficientA1=coeffs[1];//12920;
coeff.coefficientA2=coeffs[2];//55;
coeff.coefficientA3=coeffs[3];//55;
coeff.gamma=coeffs[4];//2400;
}
else
{
std::cout<<"\t\tInvalidinput,defaultvaluewillbeused."<<std::endl;
coeff.coefficientA0=31308;
coeff.coefficientA1=12920;
coeff.coefficientA2=55;
coeff.coefficientA3=55;
coeff.gamma=2400;
}
res=displayGamma->SetReGammaCoefficient(coeff);
}
returnres;
}

//SetGamma
voidSetGamma(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey)
{
//GetdisplayGamma
IADLXDisplayGammaPtrdisplayGamma;
ADLX_RESULTres=displayService->GetGamma(display,&displayGamma);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"===Setgamma==="<<std::endl;
ADLX_RESULTres=ADLX_OK;

switch(key)
{
//UsepresetcoefficientsforReGammaSRGB
case0:
res=displayGamma->SetReGammaSRGB();
break;

//UsepresetcoefficientsforDeGammaSRGB
case1:
res=SetGammaWithCustomCoeffs(displayService,display);
break;

//userampformfile,filepath:output-night-light.txt
case2:
res=displayGamma->SetReGammaRamp("output-night-light.txt");
break;

//Userampfrommemory
case3:
{
constfloatreGammaF=2.4f;
ADLX_GammaRampramp=CreateReGammaRamp(reGammaF);
res=displayGamma->SetReGammaRamp(ramp);
}
break;

//Resetramp
case4:
res=displayGamma->ResetGammaRamp();
break;

default:
break;
}
std::cout<<"\tReturncodeis:"<<res<<"(0meanssuccess)"<<std::endl;
}
}

//Mainmenu
voidMainMenu()
{
std::cout<<"\tChooseonefromthefollowingoptions"<<std::endl;

std::cout<<"\t->Press1toshowdisplaygammasupport"<<std::endl;

std::cout<<"\t->Press2todisplaycurrentgammainfo"<<std::endl;

std::cout<<"\t->Press3tosetReGammaSRGBusingpredefinedcoefficients"<<std::endl;
std::cout<<"\t->Press4tosetReGammausingcustomcoefficients"<<std::endl;
std::cout<<"\t->Press5tosetReGammausingrampfromfile[filepath:../output-night-light.txt]"<<std::endl;
std::cout<<"\t->Press6tosetReGammausingrampfrommemory"<<std::endl;
std::cout<<"\t->Press7toresetGammaramp"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaythemainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//ShowGammasupport
case'1':
ShowDisplayGammaSupport(displayService,display);
break;

//GetcurrentGammastate
case'2':
GetCurrentGammaState(displayService,display);
break;

//SetGamma
case'3':
case'4':
case'5':
case'6':
case'7':
SetGamma(displayService,display,num-'3');
break;

//Showoptionsagain
case'm':
case'M':
MainMenu();
break;

default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

ADLX_GammaRampCreateReGammaRamp(constfloatfGamma)
{
ADLX_GammaRampramp;
doubleg_fGammaRemapRGB[3]={1,1,0.5};
for(intj=0;j<3;j++)
{
for(inti=0;i<256;i++)
{

floatnAdj=i/255.0f;
if(nAdj<0.0031308f)
{
nAdj=nAdj*12.92f;
}
else
{
nAdj=(1+0.055f)*powf(nAdj,1/fGamma)-0.055f;
if(nAdj<0.0f)
nAdj=0.0f;
}
ramp.gamma[i+j*256]=(unsignedshort)(1*g_fGammaRemapRGB[j]*(int)(nAdj*0xFFFF));
}
}
returnramp;
}
