#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/IDisplayGamut.h"
#include"SDK/Include/IDisplays.h"
#include<iostream>
#include<string>

//Useadlxnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed. 
//Useglobalvariablestoensurevalidityoftheinterface. 
staticADLXHelperg_ADLXHelp;

//Showdisplaygamutsupport
voidShowDisplayGamutSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Getanddisplaygamutinformation,itcontainswhitepointandcolorspace
voidGetCurrentGamutInfo(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Setgamut(whitepointandcolorspace)
voidSetGamut(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey);

//mainmenu
voidMainMenu();

//menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//Getdisplayservice
IADLXDisplayServicesPtrdisplayService;
res=g_ADLXHelp.GetSystemServices()->GetDisplaysServices(&displayService);
if(ADLX_SUCCEEDED(res))
{
//Getdisplaylist
IADLXDisplayListPtrdisplayList;
res=displayService->GetDisplays(&displayList);
if(ADLX_SUCCEEDED(res))
{
//Inspectforthefirstdisplayinthelist
adlx_uintit=0;
IADLXDisplayPtrdisplay;
res=displayList->At(it,&display);
if(ADLX_SUCCEEDED(res))
{
//Showamenuwithoptionstochosefrom
MainMenu();
//Getandexecutethechoice
MenuControl(displayService,display);
}
}
else
{
std::cout<<"FailedtogettheDisplayslist"<<std::endl;
}
}
else
{
std::cout<<"FailedtogettheDisplayServices"<<std::endl;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pauseforuserseetheprintout
system("pause");

return0;
}

//ShowdisplayGamutsupport
voidShowDisplayGamutSupport(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//GetdisplayGamut
IADLXDisplayGamutPtrdisplayGamut;
ADLX_RESULTres=displayService->GetGamut(display,&displayGamut);
if(ADLX_SUCCEEDED(res))
{
adlx_boolsupported=false;
std::cout<<"===GamutSpacesupportedstatus==="<<std::endl;
displayGamut->IsSupportedCCIR709ColorSpace(&supported);
std::cout<<"\tIsSupportedGamut_CCIR_709:"<<supported<<std::endl;
displayGamut->IsSupportedCCIR601ColorSpace(&supported);
std::cout<<"\tIsSupportedGamut_CCIR_601:"<<supported<<std::endl;
displayGamut->IsSupportedAdobeRgbColorSpace(&supported);
std::cout<<"\tIsSupportedGamut_Adobe_RGB:"<<supported<<std::endl;
displayGamut->IsSupportedCIERgbColorSpace(&supported);
std::cout<<"\tIsSupportedGamut_CIE_RGB:"<<supported<<std::endl;
displayGamut->IsSupportedCCIR2020ColorSpace(&supported);
std::cout<<"\tIsSupportedCCIR2020ColorSpace:"<<supported<<std::endl;
displayGamut->IsSupportedCustomColorSpace(&supported);
std::cout<<"\tIsSupportedGamut_Custom:"<<supported<<std::endl;

std::cout<<"===Whitepointsupportedstatus==="<<std::endl;
displayGamut->IsSupported5000kWhitePoint(&supported);
std::cout<<"\tIsSupportedWhitePoint_5000k:"<<supported<<std::endl;
displayGamut->IsSupported6500kWhitePoint(&supported);
std::cout<<"\tIsSupportedWhitePoint_6500k:"<<supported<<std::endl;
displayGamut->IsSupported7500kWhitePoint(&supported);
std::cout<<"\tIsSupportedWhitePoint_7500k:"<<supported<<std::endl;
displayGamut->IsSupported9300kWhitePoint(&supported);
std::cout<<"\tIsSupportedWhitePoint_9300k:"<<supported<<std::endl;
displayGamut->IsSupportedCustomWhitePoint(&supported);
std::cout<<"\tIsSupportedWhitePoint_Custom:"<<supported<<std::endl;
}
}

//GetandshowGamutinformation,itcontainswhitepointandcolorspace
voidGetCurrentGamutInfo(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
//GetdisplayGamut
IADLXDisplayGamutPtrdisplayGamut;
ADLX_RESULTres=displayService->GetGamut(display,&displayGamut);
if(ADLX_SUCCEEDED(res))
{
adlx_boolapplied=false;
ADLX_RESULTres=ADLX_FAIL;
std::cout<<"===CurrntWhitepointstatus==="<<std::endl;
displayGamut->IsCurrent5000kWhitePoint(&applied);
std::cout<<"\tIsCurrentWhitePoint_5000k:"<<applied<<std::endl;
displayGamut->IsCurrent6500kWhitePoint(&applied);
std::cout<<"\tIsCurrentWhitePoint_6500k:"<<applied<<std::endl;
displayGamut->IsCurrent7500kWhitePoint(&applied);
std::cout<<"\tIsCurrentWhitePoint_7500k:"<<applied<<std::endl;
displayGamut->IsCurrent9300kWhitePoint(&applied);
std::cout<<"\tIsCurrentWhitePoint_9300k:"<<applied<<std::endl;
displayGamut->IsCurrentCustomWhitePoint(&applied);
std::cout<<"\tIsCurrentWhitePoint_Custom:"<<applied<<std::endl;
ADLX_Pointpoint={0};
res=displayGamut->GetWhitePoint(&point);
std::cout<<"\tGetWhitePoint:("<<point.x<<","<<point.y<<")"<<std::endl;

std::cout<<"===Currntgamutspacestatus==="<<std::endl;
displayGamut->IsCurrentCCIR709ColorSpace(&applied);
std::cout<<"\tIsCurrentGamut_CCIR_709:"<<applied<<std::endl;
displayGamut->IsCurrentCCIR601ColorSpace(&applied);
std::cout<<"\tIsCurrentGamut_CCIR_601:"<<applied<<std::endl;
displayGamut->IsCurrentAdobeRgbColorSpace(&applied);
std::cout<<"\tIsCurrentGamut_Adobe_RGB:"<<applied<<std::endl;
displayGamut->IsCurrentCIERgbColorSpace(&applied);
std::cout<<"\tIsCurrentGamut_CIE_RGB:"<<applied<<std::endl;
displayGamut->IsCurrentCCIR2020ColorSpace(&applied);
std::cout<<"\tIsCurrentCCIR2020ColorSpace:"<<applied<<std::endl;
displayGamut->IsCurrentCustomColorSpace(&applied);
std::cout<<"\tIsCurrentCustomColorSpace:"<<applied<<std::endl;
ADLX_GamutColorSpacegamutCoordinates={0};
res=displayGamut->GetGamutColorSpace(&gamutCoordinates);
std::cout<<"\tGetGamutColorSpace:R("<<gamutCoordinates.red.x<<","<<gamutCoordinates.red.y<<")"
<<"G("<<gamutCoordinates.green.x<<","<<gamutCoordinates.green.y<<")"
<<"B("<<gamutCoordinates.blue.x<<","<<gamutCoordinates.blue.y<<")"<<std::endl;
}
}

//SetGamut(whitepointandcolorspace)
voidSetGamut(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display,constintkey)
{
std::cout<<"===Setwhitepointandgamutspace==="<<std::endl;

ADLX_RESULTres=ADLX_FAIL;
ADLX_WHITE_POINTpredefinedWhitePoint=WHITE_POINT_6500K;
ADLX_GAMUT_SPACEpredefinedGamutSpace=GAMUT_SPACE_ADOBE_RGB;
ADLX_RGBcustomWhitePoint={0.654108,0.767112,1};
ADLX_GamutColorSpacecustomGamutSpace=
{
{6400,3300},{3000,6000},{1500,600}
};

//GetdisplayGamut
IADLXDisplayGamutPtrdisplayGamut;
res=displayService->GetGamut(display,&displayGamut);
if(ADLX_SUCCEEDED(res))
{
switch(key)
{
case0:
res=displayGamut->SetGamut(predefinedWhitePoint,predefinedGamutSpace);
break;
case1:
res=displayGamut->SetGamut(customWhitePoint,predefinedGamutSpace);
break;
case2:
res=displayGamut->SetGamut(predefinedWhitePoint,customGamutSpace);
break;
case3:
res=displayGamut->SetGamut(customWhitePoint,customGamutSpace);
break;

default:
break;
}
std::cout<<"\treturncode(0isSuccess)is:"<<res<<std::endl;
}
}

//mainmenu
voidMainMenu()
{
std::cout<<"\tChooseonefromthefollowingoptions"<<std::endl;

std::cout<<"\t->Press1toshowGamutsupport"<<std::endl;

std::cout<<"\t->Press2togetcurrentdisplayGamutinfo"<<std::endl;

std::cout<<"\t->Press3tosetpredefinedwhitepointandpredefinedGamutspace"<<std::endl;
std::cout<<"\t->Press4tosetcustomwhitepointandpredefinedGamutspace"<<std::endl;
std::cout<<"\t->Press5tosetpredefinedwhitepointandcustomGamutspace"<<std::endl;
std::cout<<"\t->Press6tosetcustomwhitepointandcustomGamutspace"<<std::endl;

std::cout<<"\t->PressQ/qtoquit"<<std::endl;
std::cout<<"\t->PressM/mtoshowtheoptionsagain"<<std::endl;
}

//menuactioncontrol
voidMenuControl(constIADLXDisplayServicesPtr&displayService,constIADLXDisplayPtr&display)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//ShowdisplayGamutsupport
case'1':
ShowDisplayGamutSupport(displayService,display);
break;

//GetGamutinformation(whitepointandcolorspace)
case'2':
GetCurrentGamutInfo(displayService,display);
break;

//SetGamut(whitepointandcolorspace)
case'3':
case'4':
case'5':
case'6':
SetGamut(displayService,display,num-'3');
break;

//Showoptionsagain
case'm':
case'M':
MainMenu();
break;

default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpauseforusertoseeitthenreturnthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}
