#include"SDK/ADLXHelper/Windows/Cpp/ADLXHelper.h"
#include"SDK/Include/ISystem1.h"
#include<chrono>
#include<iostream>
#include<thread>

//UseADLXnamespace
usingnamespaceadlx;

//ADLXHelperinstance
//NooutstandinginterfacesfromADLXmustexistwhenADLXisdestroyed.
//Useglobalvariablestoensurevalidityoftheinterface.
staticADLXHelperg_ADLXHelp;

//DisplayGPUinformation
voidShowGPUInfo(constIADLXGPUPtr&gpu);

//DisplayGPUhybridgraphictype
voidShowHybridGraphicType();

//AddGPUchangeeventlistener
voidAddGPUEventListener(constIADLXGPUPtr&gpu);

//Mainmenu
voidMainMenu();

//Menuactioncontrol
voidMenuControl(constIADLXGPUPtr&gpu);

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode);

//GPUeventchangehandlerdefinition
classGPUEventListener:publicIADLXGPUsEventListener
{
adlx_boolADLX_STD_CALLOnGPUListChanged(IADLXGPUList*pNewGPUs)
{
std::cout<<"GPUlistchangeeventreceived"<<std::endl;
if(pNewGPUs==nullptr)
{
std::cout<<"GPUlistdoesnotexist"<<std::endl;
returnfalse;
}

std::cout<<"GPUlistsize:"<<pNewGPUs->Size()<<std::endl;

returntrue;
}
};

intmain()
{
//Definereturncode
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//GetGPUlist
IADLXGPUListPtrgpus;
res=g_ADLXHelp.GetSystemServices()->GetGPUs(&gpus);
if(ADLX_SUCCEEDED(res)&&!gpus->Empty())
{
//InspectforthefirstGPUinthelist
IADLXGPUPtrgpu;
res=gpus->At(0,&gpu);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandparsetheselection
MenuControl(gpu);
}
}
else
{
std::cout<<"FailedtogettheGPUlist"<<std::endl;
}
}
else
{
returnWaitAndExit("ADLXinitializationfailed",0);
}

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

//DisplayGPUinformation
voidShowGPUInfo(constIADLXGPUPtr&gpu)
{
std::cout<<"\n====GPUinfo===="<<std::endl;
//DisplayGPUinfo
constchar*vendorId=nullptr;
ADLX_RESULTret=gpu->VendorId(&vendorId);
std::cout<<"VendorId:"<<vendorId<<"returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

ADLX_ASIC_FAMILY_TYPEasicFamilyType=ASIC_UNDEFINED;
ret=gpu->ASICFamilyType(&asicFamilyType);
std::cout<<"ASICFamilyType:"<<asicFamilyType<<"returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

ADLX_GPU_TYPEgpuType=GPUTYPE_UNDEFINED;
ret=gpu->Type(&gpuType);
std::cout<<"Type:"<<gpuType<<std::endl;

adlx_boolisExternal=false;
ret=gpu->IsExternal(&isExternal);
std::cout<<"IsExternal:"<<isExternal<<",returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

constchar*gpuName=nullptr;
ret=gpu->Name(&gpuName);
std::cout<<"Name:"<<gpuName<<",returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

constchar*driverPath=nullptr;
ret=gpu->DriverPath(&driverPath);
std::cout<<"DriverPath:"<<driverPath<<",returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

constchar*pnpString=nullptr;
ret=gpu->PNPString(&pnpString);
std::cout<<"PNPString:"<<pnpString<<",returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

adlx_boolhasDesktops=false;
ret=gpu->HasDesktops(&hasDesktops);
std::cout<<"HasDesktops:"<<hasDesktops<<",returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

adlx_uinttotalVRAM;
ret=gpu->TotalVRAM(&totalVRAM);
std::cout<<"TotalVRAM:"<<totalVRAM<<"MB"<<",returncodeis:"<<ret<<"(0meanssuccess)"<<std::endl;

adlx_intid;
ret=gpu->UniqueId(&id);
std::cout<<"UniqueId:"<<id<<std::endl;

IADLXGPU1Ptrgpu1(gpu);
if(gpu1)
{
constchar*productName=nullptr;
ret=gpu1->ProductName(&productName);
std::cout<<"ProductName:"<<productName<<std::endl;

ADLX_MGPU_MODEmode=MGPU_NONE;
ret=gpu1->MultiGPUMode(&mode);
printf("Multi-GPUMode:");
if(mode==MGPU_PRIMARY)
std::cout<<"GPUistheprimaryGPU"<<std::endl;
elseif(mode==MGPU_SECONDARY)
std::cout<<"GPUisthesecondaryGPU"<<std::endl;
else
std::cout<<"GPUisnotinMulti-GPU"<<std::endl;

ADLX_PCI_BUS_TYPEbusType=UNDEFINED;
ret=gpu1->PCIBusType(&busType);
std::cout<<"PCIBusType:"<<busType<<std::endl;

adlx_uintlaneWidth=0;
ret=gpu1->PCIBusLaneWidth(&laneWidth);
std::cout<<"PCIBusLaneWidth:"<<laneWidth<<std::endl;
}
}

voidShowHybridGraphicType()
{
ADLX_HG_TYPEhgType=ADLX_HG_TYPE::NONE;
ADLX_RESULTres=g_ADLXHelp.GetSystemServices()->HybridGraphicsType(&hgType);
if(ADLX_SUCCEEDED(res))
{
switch(hgType)
{
caseADLX_HG_TYPE::NONE:
std::cout<<"GPUhybridtypeis:"
<<"ADLX_HG_TYPE::NONE"<<std::endl;
break;
caseADLX_HG_TYPE::AMD:
std::cout<<"GPUhybridtypeis:"
<<"ADLX_HG_TYPE::AMD"<<std::endl;
break;
caseADLX_HG_TYPE::OTHER:
std::cout<<"GPUhybridtypeis:"
<<"ADLX_HG_TYPE::OTHER"<<std::endl;
break;
}
}
}

//AddGPUchangeeventlistener
voidAddGPUEventListener(constIADLXGPUPtr&gpu)
{
GPUEventListenergpuListener;
IADLXGPUsChangedHandlingPtrgpusChangedHandling;
ADLX_RESULTres=g_ADLXHelp.GetSystemServices()->GetGPUsChangedHandling(&gpusChangedHandling);
if(ADLX_SUCCEEDED(res)&&gpusChangedHandling)
{
res=gpusChangedHandling->AddGPUsListEventListener(&gpuListener);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"AdditionofGPUeventlistenersuccessful"<<std::endl;
}

//EnableordisableaGPUdeviceusingWindowsDeviceManager
std::this_thread::sleep_for(std::chrono::seconds(15));

res=gpusChangedHandling->RemoveGPUsListEventListener(&gpuListener);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"RemovalofGPUeventlistenersuccessful"<<std::endl;
}
}
}

voidMainMenu()
{
std::cout<<"\tChooseonefromthefollowingoptions"<<std::endl;

std::cout<<"\t->Press1todisplayGPUinformation"<<std::endl;

std::cout<<"\t->Press2toaddGPUeventlistener"<<std::endl;

std::cout<<"\t->Press3todisplayGPUhybridgraphictype"<<std::endl;

std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaythemainmenuoptions"<<std::endl;
}

//Menuactioncontrol
voidMenuControl(constIADLXGPUPtr&gpu)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
//DisplayGPUinformation
case'1':
ShowGPUInfo(gpu);
break;
//AddGPUchangeeventlistener
case'2':
AddGPUEventListener(gpu);
break;
//DisplayGPUhybridgraphictype
case'3':
ShowHybridGraphicType();
break;
//Displaymainmenuoptions
case'm':
case'M':
MainMenu();
break;
default:
break;
}
}
}

//Waitforexitwitherrormessage
intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}
