HANDLEsmartShiftEcoChangedBlockEvent=CreateEvent(NULL,TRUE,FALSE,NULL);

classSmartShiftEcoChangedCallBack:publicIADLXPowerTuningChangedListener
{
public:
adlx_boolADLX_STD_CALLOnPowerTuningChanged(IADLXPowerTuningChangedEvent*pPowerTuningChangedEvent)
{
IADLXPowerTuningChangedEvent1PtrpPowerTuningChangedEvent1=nullptr;
pPowerTuningChangedEvent->QueryInterface(IADLXPowerTuningChangedEvent1::IID(),reinterpret_cast<void**>(&pPowerTuningChangedEvent1));
if(pPowerTuningChangedEvent1->IsSmartShiftEcoChanged())
{
std::cout<<"\tSmartShiftEcoChanged"<<std::endl;
}
SetEvent(smartShiftEcoChangedBlockEvent);
returntrue;
}
};

intmain()
{
ADLX_RESULTres=ADLX_FAIL;

//InitializeADLX
res=g_ADLXHelp.Initialize();
if(ADLX_SUCCEEDED(res))
{
//GetIADLXSystem1interfaceviaIADLXSystem::QueryInterface
IADLXSystem2Ptrsystem2;
res=g_ADLXHelp.GetSystemServices()->QueryInterface(IADLXSystem2::IID(),reinterpret_cast<void**>(&system2));
if(ADLX_SUCCEEDED(res))
{
//AddGPUapplicationslisteventlistener
IADLXGPUAppsListChangedHandlingPtrappsListHandle;
res=system2->GetGPUAppsListChangedHandling(&appsListHandle);
IADLXGPUAppsListEventListener*gpuAppsListChangedCallBack=newGPUAppsListChangedCallBack{};
autoaddListenerResult1=ADLX_FAIL;
if(ADLX_SUCCEEDED(res))
addListenerResult1=appsListHandle->AddGPUAppsListEventListener(gpuAppsListChangedCallBack);
//Getpowertuningservice
IADLXPowerTuningServicesPtrpowerTuningService;
res=system2->GetPowerTuningServices(&powerTuningService);
if(ADLX_SUCCEEDED(res))
{
//Getpowertuningservice1
IADLXPowerTuningServices1PtrpowerTuningService1;
res=powerTuningService->QueryInterface(IADLXPowerTuningServices1::IID(),reinterpret_cast<void**>(&powerTuningService1));
if(ADLX_SUCCEEDED(res))
{
//Addpowertuningchangedlistener
IADLXPowerTuningChangedHandlingPtrpowerTuningChangedHandle;
res=powerTuningService1->GetPowerTuningChangedHandling(&powerTuningChangedHandle);
IADLXPowerTuningChangedListener*smartShiftEcoChangedCallBack=newSmartShiftEcoChangedCallBack{};
autoaddListenerResult2=ADLX_FAIL;
if(ADLX_SUCCEEDED(res))
addListenerResult2=powerTuningChangedHandle->AddPowerTuningEventListener(smartShiftEcoChangedCallBack);
//GetAMDSmartShiftEcointerface
IADLXSmartShiftEcoPtrsmartShiftEco;
res=powerTuningService1->GetSmartShiftEco(&smartShiftEco);
if(ADLX_SUCCEEDED(res))
{
//Displaymainmenuoptions
MainMenu();
//Getandexecutethechoice
MenuControl(powerTuningService1,smartShiftEco);
}
else
std::cout<<"FailedtogetAMDSmartShiftEcointerface"<<std::endl;
//Removepowertuningchangedlistener
if(ADLX_SUCCEEDED(addListenerResult2))
powerTuningChangedHandle->RemovePowerTuningEventListener(smartShiftEcoChangedCallBack);
deletesmartShiftEcoChangedCallBack;
smartShiftEcoChangedCallBack=nullptr;
}
else
std::cout<<"Failedtogetpowertuningservice1"<<std::endl;
}
else
std::cout<<"Failedtogetpowertuningservice"<<std::endl;
//RemoveGPUapplicationslisteventlistener
if(ADLX_SUCCEEDED(addListenerResult1))
appsListHandle->RemoveGPUAppsListEventListener(gpuAppsListChangedCallBack);
deletegpuAppsListChangedCallBack;
gpuAppsListChangedCallBack=nullptr;
}
else
std::cout<<"FailedtogetIADLXSystem2"<<std::endl;
}
else
returnWaitAndExit("\tg_ADLXHelpinitializefailed",0);

//DestroyADLX
res=g_ADLXHelp.Terminate();
std::cout<<"DestroyADLXres:"<<res<<std::endl;

//Pausetoseetheprintout
system("pause");

return0;
}

voidMainMenu()
{
std::cout<<"\tChoosefromthefollowingoptions:"<<std::endl;
std::cout<<"\t->Press1todisplayAMDSmartShiftEcosupport"<<std::endl;
std::cout<<"\t->Press2todisplayAMDSmartShiftEcoenabledstate"<<std::endl;
std::cout<<"\t->Press3tosetAMDSmartShiftEcoenabledstate"<<std::endl;
std::cout<<"\t->Press4todisplayAMDSmartShiftEcoinactivestate"<<std::endl;
std::cout<<"\t->Press5todisplayAMDGPUconnectsupport"<<std::endl;
std::cout<<"\t->Press6todisplayGPUpowerstate"<<std::endl;
std::cout<<"\t->Press7topoweronaGPU"<<std::endl;
std::cout<<"\t->Press8topoweroffaGPU"<<std::endl;
std::cout<<"\t->Press9toabortpoweroffaGPU"<<std::endl;
std::cout<<"\t->PressAtosetpowercontroltodefault"<<std::endl;
std::cout<<"\t->PressBtodisplaygetapplicationlistsupport"<<std::endl;
std::cout<<"\t->PressCtogetapplicationlist"<<std::endl;
std::cout<<"\t->PressQ/qtoterminatetheapplication"<<std::endl;
std::cout<<"\t->PressM/mtodisplaymainmenuoptions"<<std::endl;
}

voidMenuControl(IADLXPowerTuningServices1PtrpowerTuningServices,IADLXSmartShiftEcoPtrsmartShiftEco)
{
intnum=0;
while((num=getchar())!='q'&&num!='Q')
{
switch(num)
{
case'1'://DisplayAMDSmartShiftEcosupport
ShowSmartShiftEcoSupport(smartShiftEco);
break;

case'2'://DisplayAMDSmartShiftEcoenabledstate
ShowSmartShiftEcoEnabledState(smartShiftEco);
break;

case'3'://SetAMDSmartShiftEcoenabledstate
SetSmartShiftEcoEnabledState(smartShiftEco);
break;

case'4'://DisplayAMDSmartShiftEcoinactivestate
ShowSmartShiftEcoInactiveState(smartShiftEco);
break;

case'5'://DisplayAMDGPUconnectsupport
ShowGPUConnectSupport(powerTuningServices);
break;

case'A'://Setpowercontroltodefault
SetPowerControlToDefault(smartShiftEco);
break;

case'6':
case'7':
case'8':
case'9':
case'B':
case'C':
UseGPUConnectFunctions(powerTuningServices,num);
break;

case'm'://Displaymenuoptions
case'M':
MainMenu();
break;

default:
break;
}
}
}

intWaitAndExit(constchar*msg,constintretCode)
{
//Printoutthemessageandpausetoseeitbeforereturningthedesiredcode
if(nullptr!=msg)
std::cout<<msg<<std::endl;

system("pause");
returnretCode;
}

//DisplayAMDSmartShiftEcosupport
voidShowSmartShiftEcoSupport(IADLXSmartShiftEcoPtrsmartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->IsSupported(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsAMDSmartShiftEcosupported:"<<supported<<std::endl;
}

//DisplayAMDSmartShiftEcoenabledstate
voidShowSmartShiftEcoEnabledState(IADLXSmartShiftEcoPtrsmartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->IsSupported(&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
adlx_boolenabled=false;
ADLX_RESULTres=smartShiftEco->IsEnabled(&enabled);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsAMDSmartShiftEcoenabled:"<<enabled<<std::endl;
}
}

//SetAMDSmartShiftEcoenabledstate
voidSetSmartShiftEcoEnabledState(IADLXSmartShiftEcoPtrsmartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->IsSupported(&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
adlx_boolenabled=false;
ADLX_RESULTres=smartShiftEco->IsEnabled(&enabled);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tCurrentAMDSmartShiftEcoenablestateis:"<<enabled<<std::endl;
res=smartShiftEco->SetEnabled(!enabled);
if(ADLX_SUCCEEDED(res))
{
WaitForSingleObject(smartShiftEcoChangedBlockEvent,5000);
ResetEvent(smartShiftEcoChangedBlockEvent);
std::cout<<"\tSetAMDSmartShiftEcoenabledstateto:"<<!enabled<<std::endl;
}
res=smartShiftEco->IsEnabled(&enabled);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tAfterset,theAMDSmartShiftEcoenablestateis:"<<enabled<<std::endl;
}
}

//DisplayAMDSmartShiftEcoinactivestate
voidShowSmartShiftEcoInactiveState(IADLXSmartShiftEcoPtrsmartShiftEco)
{
adlx_boolsupported=false;
ADLX_RESULTres=smartShiftEco->IsSupported(&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
adlx_boolenabled=false;
ADLX_RESULTres=smartShiftEco->IsEnabled(&enabled);
if(ADLX_SUCCEEDED(res)&&enabled)
{
adlx_boolinactive=false;
ADLX_RESULTres=smartShiftEco->IsInactive(&inactive);
std::cout<<"\tIsAMDSmartShiftEcoinactive:"<<inactive<<std::endl;
if(inactive)
{
ADLX_SMARTSHIFT_ECO_INACTIVE_REASONreason=INACTIVE_REASON_UNKNOWN;
ADLX_RESULTres=smartShiftEco->GetInactiveReason(&reason);
if(ADLX_SUCCEEDED(res))
{
std::cout<<"\tAMDSmartShiftEcoinactivereason:"<<reason<<std::endl;
}
}
}
}
}

//DisplayAMDGPUconnectsupport
voidShowGPUConnectSupport(IADLXPowerTuningServices1PtrpowerTuningService)
{
adlx_boolsupported=false;
ADLX_RESULTres=powerTuningService->IsGPUConnectSupported(&supported);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsGPUconnectsupported:"<<supported<<std::endl;
}

//UsefunctionsofGPUconnect
voidUseGPUConnectFunctions(IADLXPowerTuningServices1PtrpowerTuningService,charnum)
{
adlx_boolsupported=false;
ADLX_RESULTres=powerTuningService->IsGPUConnectSupported(&supported);
if(ADLX_SUCCEEDED(res)&&supported)
{
IADLXGPU2ListPtrgpuList=nullptr;
res=powerTuningService->GetGPUConnectGPUs(&gpuList);
if(ADLX_SUCCEEDED(res)&&!gpuList->Empty())
{
//UsethefirstGPU
IADLXGPU2Ptrgpu=nullptr;
res=gpuList->At(gpuList->Begin(),&gpu);
if(ADLX_SUCCEEDED(res))
{
switch(num)
{
case'6'://DisplayGPUpowerstate
ShowGPUPowerState(gpu);
break;

case'7'://PoweronaGPU
PowerOnGPU(gpu);
break;

case'8'://PoweroffaGPU
PowerOffGPU(gpu);
break;

case'9'://AbortpoweroffaGPU
AbortPowerOffGPU(gpu);
break;

case'B'://Displaygetapplicationlistsupport
ShowGetApplicationListSupport(gpu);
break;

case'C'://Getapplicationlist
GetApplicationList(gpu);
break;

default:
break;
}

}
}
else
std::cout<<"\tNotfoundGPUsupportGPUconnect"<<std::endl;
}
else
std::cout<<"\tGPUconnectisnotsupported"<<std::endl;
}

//DisplayGPUpowerstate
voidShowGPUPowerState(IADLXGPU2Ptrgpu)
{
adlx_boolstate=false;
ADLX_RESULTres=gpu->IsPowerOff(&state);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tIsGPUpoweroff:"<<state<<std::endl;
}

//PoweronaGPU
voidPowerOnGPU(IADLXGPU2Ptrgpu)
{
adlx_boolstate=false;
ADLX_RESULTres=gpu->IsPowerOff(&state);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tCurrenttheGPUpowerstateis:"<<(state?"OFF":"ON")<<std::endl;
res=gpu->PowerOn();
if(ADLX_SUCCEEDED(res))
std::cout<<"\tPowerontheGPU"<<std::endl;
res=gpu->IsPowerOff(&state);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tAfterset,theGPUpowerstateis:"<<(state?"OFF":"ON")<<std::endl;
}

classGPUConnectChangedCallBack:publicIADLXGPUConnectChangedListener
{
public:
adlx_boolADLX_STD_CALLOnGPUConnectChanged(IADLXGPUConnectChangedEvent*pGPUConnectChangedEvent)
{
if(pGPUConnectChangedEvent->IsGPUPowerChanged())
std::cout<<"\tGPUpowerchanged"<<std::endl;
if(pGPUConnectChangedEvent->IsGPUAppsListChanged())
{
std::cout<<"\tGPUapplicationslistchanged"<<std::endl;
IADLXGPU2Ptrgpu;
ADLX_RESULTres=pGPUConnectChangedEvent->GetGPU(&gpu);
if(ADLX_SUCCEEDED(res))
{
IADLXApplicationListPtrappList;
res=gpu->GetApplications(&appList);
if(ADLX_SUCCEEDED(res))
{
for(autoit=appList->Begin();it!=appList->End();++it)
{
IADLXApplicationPtrapp;
res=appList->At(it,&app);
if(ADLX_SUCCEEDED(res))
{
adlx_ulongpid=0;
res=app->ProcessID(&pid);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tTheprocessIDoftheprocessrunningontheGPUis:"<<pid<<std::endl;

constwchar_t*name=nullptr;
res=app->Name(&name);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tThenameoftheprocessrunningontheGPUis:"<<name<<std::endl;

constwchar_t*fullPath=nullptr;
res=app->FullPath(&fullPath);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tThefullpathoftheprocessrunningontheGPUis:"<<fullPath<<std::endl;

ADLX_APP_GPU_DEPENDENCYtype=ADLX_APP_GPU_DEPENDENCY::APP_GPU_UNKNOWN;
res=app->GPUDependencyType(&type);
if(ADLX_SUCCEEDED(res))
{
if(type==ADLX_APP_GPU_DEPENDENCY::APP_GPU_BOUND)
std::cout<<"\tTheGPUdependencytypeoftheprocessrunningontheGPUis:APP_GPU_BOUND"<<std::endl;
elseif(type==ADLX_APP_GPU_DEPENDENCY::APP_GPU_NOT_BOUND)
std::cout<<"\tTheGPUdependencytypeoftheprocessrunningontheGPUis:APP_GPU_NOT_BOUND"<<std::endl;
else
std::cout<<"\tTheGPUdependencytypeoftheprocessrunningontheGPUis:APP_GPU_UNKNOWN"<<std::endl;
}
}
}
}
}
}
ADLX_RESULTres=ADLX_FAIL;
if(pGPUConnectChangedEvent->IsGPUPowerChangeError(&res))
{
if(res==ADLX_TIMEOUT_OPERATION)
std::cout<<"\tGPUpowerchangeerror,timeoutoperation"<<std::endl;
else
std::cout<<"\tGPUpowerchangeerror,resultis:"<<res<<std::endl;
}

returntrue;
}
};

staticGPUConnectChangedCallBackcallBack;

//PoweroffaGPU
voidPowerOffGPU(IADLXGPU2Ptrgpu)
{
adlx_boolstate=false;
ADLX_RESULTres=gpu->IsPowerOff(&state);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tCurrenttheGPUpowerstateis:"<<(state?"OFF":"ON")<<std::endl;

res=gpu->StartPowerOff(&callBack,10000);
if(ADLX_SUCCEEDED(res))
std::cout<<"\tPowerofftheGPU"<<std::endl;
elseif(res==ADLX_GPU_IN_USE)
{
std::cout<<"\tStartpowerofftheGPU,butGPUisinuse,waitfor10seconds"<<std::endl;
std::cout<<"\tPress9canabortpoweroffGPUduringthistime"<<std::endl;
}
}

//AbortpoweroffaGPU
voidAbortPowerOffGPU(IADLXGPU2Ptrgpu)
{
ADLX_RESULTres=gpu->AbortPowerOff();
if(ADLX_SUCCEEDED(res))
std::cout<<"\tAbortpoweroffGPUsuccessfully"<<std::endl;
}

