//Callback
classDisplayListCallBack:publicIADLXDisplayListChangedListener
{
public:

adlx_boolOnDisplayListChanged(IADLXDisplayList*pNewDisplay)override{
std::vector<IADLXDisplayPtr>list;
for(adlx_uints=pNewDisplay->Begin();s!=pNewDisplay->End();s++){
IADLXDisplayPtritem;
pNewDisplay->At(s,&item);
list.emplace_back(item);
}
if(m_call){
returnm_call(list);
}
returntrue;
}

std::function<bool(std::vector<IADLXDisplayPtr>)>m_call;
};

